import { useState } from "react";
import { useParams, useLocation } from "wouter";
import { motion, AnimatePresence } from "framer-motion";
import {
  ArrowLeft,
  Users,
  CheckCircle2,
  XCircle,
  AlertTriangle,
  Workflow,
  Clock,
  Plus,
  Minus,
  Pencil,
  Check,
  X,
} from "lucide-react";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { useI18n } from "@/i18n/i18n";
import { useGroups } from "@/contexts/GroupsContext";

function StatusBadge({ status }: { status: "success" | "partial" | "failed" }) {
  const { t } = useI18n();
  const config = {
    success: {
      label: t("status.success"),
      className: "bg-green-500/15 text-green-400 border-green-500/30",
      icon: <CheckCircle2 className="w-3 h-3" />,
    },
    partial: {
      label: t("status.partial"),
      className: "bg-yellow-500/15 text-yellow-400 border-yellow-500/30",
      icon: <AlertTriangle className="w-3 h-3" />,
    },
    failed: {
      label: t("status.failed"),
      className: "bg-red-500/15 text-red-400 border-red-500/30",
      icon: <XCircle className="w-3 h-3" />,
    },
  };
  const c = config[status];
  return (
    <Badge variant="outline" className={`${c.className} gap-1 no-default-hover-elevate`}>
      {c.icon}
      {c.label}
    </Badge>
  );
}

export default function GroupDetail() {
  const { id } = useParams<{ id: string }>();
  const [, navigate] = useLocation();
  const { t } = useI18n();
  const {
    getGroupById,
    getGroupAccounts,
    getUnassignedAccounts,
    addAccountToGroup,
    removeAccountFromGroup,
    renameGroup,
  } = useGroups();

  const group = getGroupById(id || "");
  const groupAccounts = getGroupAccounts(id || "");
  const unassigned = getUnassignedAccounts();

  const [editing, setEditing] = useState(false);
  const [editName, setEditName] = useState("");
  const [showManage, setShowManage] = useState(false);

  if (!group) {
    return (
      <div className="space-y-8">
        <Button
          variant="ghost"
          onClick={() => navigate("/groups")}
          className="gap-2 text-muted-foreground"
          data-testid="button-back-to-groups"
        >
          <ArrowLeft className="w-4 h-4" />
          {t("groups.backToGroups")}
        </Button>
        <div className="text-center py-20 text-muted-foreground">
          {t("groups.notFound")}
        </div>
      </div>
    );
  }

  const successCount = Math.round(groupAccounts.length * (group.successRate / 100));
  const failedCount = groupAccounts.length - successCount;

  return (
    <div className="space-y-8">
      <div className="flex items-center gap-4 flex-wrap">
        <Button
          variant="ghost"
          onClick={() => navigate("/groups")}
          className="gap-2 text-muted-foreground"
          data-testid="button-back-to-groups"
        >
          <ArrowLeft className="w-4 h-4" />
          {t("groups.backToGroups")}
        </Button>
      </div>

      <div className="flex items-center justify-between gap-4 flex-wrap">
        <div className="flex items-center gap-3">
          {editing ? (
            <div className="flex items-center gap-2">
              <Input
                value={editName}
                onChange={(e) => setEditName(e.target.value)}
                className="bg-white/5 border-white/10 text-white h-10 rounded-xl w-64"
                onKeyDown={(e) => {
                  if (e.key === "Enter" && editName.trim()) {
                    renameGroup(group.id, editName.trim());
                    setEditing(false);
                  }
                  if (e.key === "Escape") setEditing(false);
                }}
                autoFocus
                data-testid="input-rename-group-detail"
              />
              <Button
                size="icon"
                variant="ghost"
                onClick={() => {
                  if (editName.trim()) renameGroup(group.id, editName.trim());
                  setEditing(false);
                }}
                data-testid="button-save-rename-detail"
              >
                <Check className="w-4 h-4" />
              </Button>
              <Button
                size="icon"
                variant="ghost"
                onClick={() => setEditing(false)}
                data-testid="button-cancel-rename-detail"
              >
                <X className="w-4 h-4" />
              </Button>
            </div>
          ) : (
            <>
              <h1 className="text-3xl font-bold" data-testid="text-group-detail-name">
                {group.name}
              </h1>
              <Button
                size="icon"
                variant="ghost"
                onClick={() => {
                  setEditName(group.name);
                  setEditing(true);
                }}
                data-testid="button-rename-group-detail"
              >
                <Pencil className="w-4 h-4" />
              </Button>
            </>
          )}
        </div>
        <StatusBadge status={group.status} />
      </div>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        {[
          { label: t("groups.totalInGroup"), value: groupAccounts.length, icon: Users, color: "text-cyan-400", bg: "bg-cyan-500/10", border: "border-cyan-500/20" },
          { label: t("groups.successfulRuns"), value: successCount, icon: CheckCircle2, color: "text-green-400", bg: "bg-green-500/10", border: "border-green-500/20" },
          { label: t("groups.failedRuns"), value: failedCount, icon: XCircle, color: "text-red-400", bg: "bg-red-500/10", border: "border-red-500/20" },
          { label: t("groups.activeWorkflows"), value: group.activeWorkflows, icon: Workflow, color: "text-purple-400", bg: "bg-purple-500/10", border: "border-purple-500/20" },
        ].map((stat, i) => (
          <motion.div
            key={stat.label}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: i * 0.1 }}
          >
            <Card
              className={`bg-white/[0.03] border ${stat.border} p-6 rounded-2xl`}
              data-testid={`stat-group-${i}`}
            >
              <div className="flex items-center justify-between mb-4">
                <span className="text-sm text-muted-foreground">{stat.label}</span>
                <div className={`w-10 h-10 rounded-xl ${stat.bg} flex items-center justify-center`}>
                  <stat.icon className={`w-5 h-5 ${stat.color}`} />
                </div>
              </div>
              <span className="text-3xl font-bold">{stat.value}</span>
            </Card>
          </motion.div>
        ))}
      </div>

      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.3 }}
      >
        <Card className="bg-white/[0.03] border border-white/10 rounded-2xl p-6">
          <div className="flex items-center justify-between mb-6 gap-4 flex-wrap">
            <h3 className="text-lg font-bold">{t("groups.groupAccounts")}</h3>
            <Button
              size="sm"
              variant="outline"
              className="border-white/10 gap-1"
              onClick={() => setShowManage(!showManage)}
              data-testid="button-toggle-manage"
            >
              {showManage ? t("common.close") : t("groups.manageAccounts")}
            </Button>
          </div>

          <AnimatePresence>
            {showManage && (
              <motion.div
                initial={{ height: 0, opacity: 0 }}
                animate={{ height: "auto", opacity: 1 }}
                exit={{ height: 0, opacity: 0 }}
                transition={{ duration: 0.3 }}
                className="overflow-hidden mb-6"
              >
                <div className="border border-white/5 rounded-xl p-4 mb-4">
                  <h4 className="text-sm font-medium text-muted-foreground mb-3">
                    {t("groups.availableAccounts")}
                  </h4>
                  {unassigned.length === 0 ? (
                    <p className="text-xs text-muted-foreground/60">{t("groups.noAvailable")}</p>
                  ) : (
                    <div className="space-y-1">
                      {unassigned.map((acc) => (
                        <div
                          key={acc.id}
                          className="flex items-center justify-between py-2 px-3 rounded-lg hover:bg-white/[0.03] transition-colors"
                          data-testid={`available-account-${acc.id}`}
                        >
                          <div className="flex items-center gap-2">
                            <span className="text-sm text-white/80">{acc.name}</span>
                            <span className="text-xs text-muted-foreground font-mono">{acc.wallet}</span>
                          </div>
                          <Button
                            size="sm"
                            variant="outline"
                            className="border-green-500/20 text-green-400 text-xs gap-1"
                            onClick={() => addAccountToGroup(group.id, acc.id)}
                            data-testid={`button-add-account-${acc.id}`}
                          >
                            <Plus className="w-3 h-3" />
                            {t("groups.addToGroup")}
                          </Button>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </motion.div>
            )}
          </AnimatePresence>

          {groupAccounts.length === 0 ? (
            <p className="text-center py-8 text-muted-foreground text-sm">
              {t("groups.noAccountsInGroup")}
            </p>
          ) : (
            <div className="space-y-1">
              {groupAccounts.map((acc) => (
                <div
                  key={acc.id}
                  className="flex items-center justify-between py-2.5 px-3 rounded-lg hover:bg-white/[0.03] transition-colors"
                  data-testid={`group-account-${acc.id}`}
                >
                  <div className="flex items-center gap-2">
                    <div className="w-8 h-8 rounded-lg bg-primary/10 flex items-center justify-center flex-shrink-0">
                      <Users className="w-4 h-4 text-primary" />
                    </div>
                    <div>
                      <span className="text-sm font-medium text-white/80">{acc.name}</span>
                      <p className="text-xs text-muted-foreground font-mono">{acc.wallet}</p>
                    </div>
                  </div>
                  <Button
                    size="sm"
                    variant="outline"
                    className="border-red-500/20 text-red-400 text-xs gap-1"
                    onClick={() => removeAccountFromGroup(group.id, acc.id)}
                    data-testid={`button-remove-account-${acc.id}`}
                  >
                    <Minus className="w-3 h-3" />
                    {t("groups.removeFromGroup")}
                  </Button>
                </div>
              ))}
            </div>
          )}
        </Card>
      </motion.div>

      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.4 }}
      >
        <Card className="bg-white/[0.03] border border-white/10 rounded-2xl p-6">
          <h3 className="text-lg font-bold mb-4">{t("groups.recentExecutions")}</h3>
          {group.executions.length === 0 ? (
            <p className="text-center py-8 text-muted-foreground text-sm">
              {t("groups.noExecutions")}
            </p>
          ) : (
            <div className="space-y-2">
              {group.executions.map((exec) => (
                <div
                  key={exec.id}
                  className="flex items-center justify-between py-3 px-4 rounded-xl bg-white/[0.02] border border-white/5"
                  data-testid={`execution-${exec.id}`}
                >
                  <div className="flex items-center gap-3">
                    <StatusBadge status={exec.status as "success" | "partial" | "failed"} />
                    <div>
                      <span className="text-sm font-medium text-white/80">{exec.workflow}</span>
                      <div className="flex items-center gap-2 mt-0.5">
                        <span className="text-xs text-muted-foreground flex items-center gap-1">
                          <Clock className="w-3 h-3" />
                          {exec.time}
                        </span>
                        <span className="text-xs text-muted-foreground">
                          {exec.accounts} {t("dashboard.accounts")}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </Card>
      </motion.div>
    </div>
  );
}

