import { useState, useRef, useCallback, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import {
  Save, Play, Download, Upload, ZoomIn, ZoomOut, Trash2,
  Undo2, Redo2, Wallet, Clock, GitFork, Repeat, GitBranch,
  Gift, ArrowLeftRight, Code, X, ChevronLeft, ChevronRight,
  GripVertical, Zap, Globe, Server, Shuffle, Users, RefreshCw,
  FileText, Plus, ArrowLeft, Layout, Check, Power, Link as LinkIcon,
  AlertTriangle, History, ChevronDown, Settings2, Target, Timer,
  Filter, CheckCircle2, XCircle, Shield, Activity, Eye
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { useI18n } from "@/i18n/i18n";
import { useGroups } from "@/contexts/GroupsContext";
import CardSettingsGuide from "@/components/CardSettingsGuide";
import { PortalSelect, PortalSelectMini, RiskBadgeSelect } from "@/components/PortalSelect";
import type { SelectOption } from "@/components/PortalSelect";
import { HelpCircle, BookOpen } from "lucide-react";

interface Port {
  id: string;
  type: "input" | "output";
}

interface WorkflowNode {
  id: string;
  type: string;
  label: string;
  icon: string;
  x: number;
  y: number;
  config: Record<string, any>;
  ports: Port[];
  enabled: boolean;
}

interface Connection {
  id: string;
  fromNode: string;
  fromPort: string;
  toNode: string;
  toPort: string;
}

interface DragState {
  type: "node" | "canvas" | "connecting" | "library-drag" | null;
  nodeId?: string;
  offsetX?: number;
  offsetY?: number;
  startX?: number;
  startY?: number;
  fromNode?: string;
  fromPort?: string;
  fromPortType?: "input" | "output";
  mouseX?: number;
  mouseY?: number;
  libraryType?: string;
}

interface SavedWorkflow {
  id: string;
  name: string;
  nodes: WorkflowNode[];
  connections: Connection[];
  lastEdited: string;
  runCount: number;
  status: "active" | "draft" | "paused";
}

interface VersionSnapshot {
  id: string;
  timestamp: string;
  nodes: WorkflowNode[];
  connections: Connection[];
}

const GRID_SIZE = 20;
const snapToGrid = (v: number) => Math.round(v / GRID_SIZE) * GRID_SIZE;

const NODE_TYPES_RAW = [
  { type: "wallet", labelKey: "nodeType.wallet", icon: "wallet", color: "#3b82f6" },
  { type: "delay", labelKey: "nodeType.delay", icon: "clock", color: "#f59e0b" },
