function getIcon(icon: string, className: string = "w-4 h-4") {
  switch (icon) {
    case "wallet": return <Wallet className={className} />;
    case "clock": return <Clock className={className} />;
    case "gitfork": return <GitFork className={className} />;
    case "repeat": return <Repeat className={className} />;
    case "gitbranch": return <GitBranch className={className} />;
    case "gift": return <Gift className={className} />;
    case "swap": return <ArrowLeftRight className={className} />;
    case "code": return <Code className={className} />;
    case "globe": return <Globe className={className} />;
    case "server": return <Server className={className} />;
    case "shuffle": return <Shuffle className={className} />;
    case "users": return <Users className={className} />;
    case "refresh": return <RefreshCw className={className} />;
    case "filetext": return <FileText className={className} />;
    default: return <Zap className={className} />;
  }
}

function getNodeColor(type: string): string {
  return NODE_TYPES_RAW.find(n => n.type === type)?.color || "#3b82f6";
}

function getScheduleLabel(config: Record<string, any>): string {
  if (config.interval && config.duration) {
    return `Every ${config.interval}${config.intervalUnit || "h"} for ${config.duration} days`;
  }
  if (config.interval) {
    return `Every ${config.interval}${config.intervalUnit || "h"}`;
  }
  return "Configure schedule";
}

let idCounter = 0;
function uid() {
  return `node_${Date.now()}_${++idCounter}`;
}

const STORAGE_KEY = "cetra_workflows";
const AUTOSAVE_KEY = "cetra_autosave";
const VERSION_KEY = "cetra_versions";

function loadWorkflows(): SavedWorkflow[] {
  try {
    const raw = localStorage.getItem(STORAGE_KEY);
    return raw ? JSON.parse(raw) : [];
  } catch { return []; }
}

function saveWorkflows(workflows: SavedWorkflow[]) {
  localStorage.setItem(STORAGE_KEY, JSON.stringify(workflows));
}

function loadVersions(): VersionSnapshot[] {
  try {
    const raw = localStorage.getItem(VERSION_KEY);
    return raw ? JSON.parse(raw) : [];
  } catch { return []; }
}

function saveVersions(versions: VersionSnapshot[]) {
  localStorage.setItem(VERSION_KEY, JSON.stringify(versions.slice(-20)));
}

