export default function WorkflowBuilder() {
  const { t } = useI18n();
  const { groups } = useGroups();
  const [view, setView] = useState<"library" | "canvas">(() => {
    try {
      const raw = localStorage.getItem(AUTOSAVE_KEY);
      if (raw) return "canvas";
    } catch {}
    return "library";
  });
  const [savedWorkflows, setSavedWorkflows] = useState<SavedWorkflow[]>(loadWorkflows);
  const [activeWorkflowId, setActiveWorkflowId] = useState<string | null>(() => {
    try {
      const raw = localStorage.getItem(AUTOSAVE_KEY);
      if (raw) { const d = JSON.parse(raw); return d.id || null; }
    } catch {}
    return null;
  });
  const [workflowName, setWorkflowName] = useState(() => {
    try {
      const raw = localStorage.getItem(AUTOSAVE_KEY);
      if (raw) { const d = JSON.parse(raw); return d.name || "Untitled Workflow"; }
    } catch {}
    return "Untitled Workflow";
  });
  const [showTemplateModal, setShowTemplateModal] = useState(false);
  const [showVersionHistory, setShowVersionHistory] = useState(false);
  const [showGuide, setShowGuide] = useState(false);
  const [guideInitialSection, setGuideInitialSection] = useState<string | undefined>(undefined);
  const [saveFlash, setSaveFlash] = useState(false);

  const [nodes, setNodes] = useState<WorkflowNode[]>(() => {
    try {
      const raw = localStorage.getItem(AUTOSAVE_KEY);
      if (raw) { const d = JSON.parse(raw); return d.nodes || []; }
    } catch {}
    return [];
  });
  const [connections, setConnections] = useState<Connection[]>(() => {
    try {
      const raw = localStorage.getItem(AUTOSAVE_KEY);
      if (raw) { const d = JSON.parse(raw); return d.connections || []; }
    } catch {}
    return [];
  });
  const [selectedNode, setSelectedNode] = useState<string | null>(null);
  const [camera, setCamera] = useState({ x: 0, y: 0 });
  const [zoom, setZoom] = useState(1);
  const [drag, setDrag] = useState<DragState>({ type: null });
  const [history, setHistory] = useState<{ nodes: WorkflowNode[]; connections: Connection[] }[]>([]);
  const [historyIndex, setHistoryIndex] = useState(-1);
  const [simulating, setSimulating] = useState(false);
  const [simulationStep, setSimulationStep] = useState(0);
  const [leftPanelOpen, setLeftPanelOpen] = useState(true);
  const [rightPanelOpen, setRightPanelOpen] = useState(false);
  const [hoveredConnection, setHoveredConnection] = useState<string | null>(null);
  const [versions, setVersions] = useState<VersionSnapshot[]>(loadVersions);
  const [spaceHeld, setSpaceHeld] = useState(false);
  const [expandedNodes, setExpandedNodes] = useState<Set<string>>(new Set());
  const [advancedNodes, setAdvancedNodes] = useState<Set<string>>(new Set());
  const [nodeSimulating, setNodeSimulating] = useState<string | null>(null);
  const [logExpandedNodes, setLogExpandedNodes] = useState<Set<string>>(new Set());
  const [collapsedSections, setCollapsedSections] = useState<Record<string, Set<string>>>({});
  const canvasRef = useRef<HTMLDivElement>(null);

  const pushHistory = useCallback((n: WorkflowNode[], c: Connection[]) => {
    setHistory(prev => {
      const newHist = prev.slice(0, historyIndex + 1);
      newHist.push({ nodes: JSON.parse(JSON.stringify(n)), connections: JSON.parse(JSON.stringify(c)) });
      return newHist;
    });
    setHistoryIndex(prev => prev + 1);
  }, [historyIndex]);

  const undo = useCallback(() => {
    if (historyIndex > 0) {
      const prev = history[historyIndex - 1];
      setNodes(JSON.parse(JSON.stringify(prev.nodes)));
      setConnections(JSON.parse(JSON.stringify(prev.connections)));
      setHistoryIndex(historyIndex - 1);
    }
  }, [history, historyIndex]);

  const redo = useCallback(() => {
    if (historyIndex < history.length - 1) {
      const next = history[historyIndex + 1];
      setNodes(JSON.parse(JSON.stringify(next.nodes)));
      setConnections(JSON.parse(JSON.stringify(next.connections)));
      setHistoryIndex(historyIndex + 1);
    }
  }, [history, historyIndex]);

  const addNode = useCallback((type: string, x: number, y: number) => {
    const def = NODE_TYPES_RAW.find(n => n.type === type);
    if (!def) return;
    const newNode: WorkflowNode = {
      id: uid(),
      type,
      label: t(def.labelKey),
      icon: def.icon,
      x: snapToGrid(x),
      y: snapToGrid(y),
      config: {
        interval: type === "delay" || type === "randomDelay" ? 4 : undefined,
        intervalUnit: "h",
        duration: type === "delay" || type === "randomDelay" ? 7 : undefined,
        percentage: type === "split" || type === "multiSplit" ? 50 : undefined,
        splitCount: type === "split" || type === "multiSplit" ? 2 : undefined,
        executionMode: "sequential",
        randomize: false,
        errorHandling: "retry",
        retryAttempts: 3,
        linkUrl: type === "website" || type === "apiCall" ? "" : undefined,
      },
      ports: [
        { id: "in", type: "input" },
        { id: "out", type: "output" },
      ],
      enabled: true,
    };
    const newNodes = [...nodes, newNode];
    setNodes(newNodes);
    pushHistory(newNodes, connections);
    setSelectedNode(newNode.id);
    setRightPanelOpen(true);
  }, [nodes, connections, pushHistory, t]);

  const deleteNode = useCallback((nodeId: string) => {
    const newNodes = nodes.filter(n => n.id !== nodeId);
    const newConns = connections.filter(c => c.fromNode !== nodeId && c.toNode !== nodeId);
    setNodes(newNodes);
    setConnections(newConns);
    pushHistory(newNodes, newConns);
    if (selectedNode === nodeId) {
      setSelectedNode(null);
      setRightPanelOpen(false);
    }
  }, [nodes, connections, selectedNode, pushHistory]);

  const clearCanvas = useCallback(() => {
    setNodes([]);
    setConnections([]);
    setSelectedNode(null);
    setRightPanelOpen(false);
    pushHistory([], []);
  }, [pushHistory]);

  const configHistoryTimer = useRef<ReturnType<typeof setTimeout> | null>(null);
  const debouncedPushHistory = useCallback((n: WorkflowNode[], c: Connection[]) => {
    if (configHistoryTimer.current) clearTimeout(configHistoryTimer.current);
    configHistoryTimer.current = setTimeout(() => {
      pushHistory(n, c);
    }, 500);
  }, [pushHistory]);

  const updateNodeConfig = useCallback((nodeId: string, key: string, value: any) => {
    setNodes(prev => {
      const updated = prev.map(n => n.id === nodeId ? { ...n, config: { ...n.config, [key]: value } } : n);
      debouncedPushHistory(updated, connections);
      return updated;
    });
  }, [connections, debouncedPushHistory]);

  const renameNode = useCallback((nodeId: string, label: string) => {
    setNodes(prev => {
      const updated = prev.map(n => n.id === nodeId ? { ...n, label } : n);
      debouncedPushHistory(updated, connections);
      return updated;
    });
  }, [connections, debouncedPushHistory]);

  const toggleNodeEnabled = useCallback((nodeId: string) => {
    setNodes(prev => {
      const updated = prev.map(n => n.id === nodeId ? { ...n, enabled: !n.enabled } : n);
      pushHistory(updated, connections);
      return updated;
    });
  }, [connections, pushHistory]);

  const saveWorkflow = useCallback(() => {
    const now = new Date().toISOString();
    const snapshot: VersionSnapshot = {
      id: `v_${Date.now()}`,
      timestamp: now,
      nodes: JSON.parse(JSON.stringify(nodes)),
      connections: JSON.parse(JSON.stringify(connections)),
    };
    const newVersions = [...versions, snapshot];
    setVersions(newVersions);
    saveVersions(newVersions);

    const existing = savedWorkflows.find(w => w.id === activeWorkflowId);
    const wf: SavedWorkflow = {
      id: activeWorkflowId || `wf_${Date.now()}`,
      name: workflowName,
      nodes: JSON.parse(JSON.stringify(nodes)),
      connections: JSON.parse(JSON.stringify(connections)),
      lastEdited: now,
      runCount: existing?.runCount || 0,
      status: "draft",
    };
    const newList = existing
      ? savedWorkflows.map(w => w.id === wf.id ? wf : w)
      : [...savedWorkflows, wf];
    setSavedWorkflows(newList);
    saveWorkflows(newList);
    setActiveWorkflowId(wf.id);
    setSaveFlash(true);
    setTimeout(() => setSaveFlash(false), 1500);
  }, [nodes, connections, workflowName, activeWorkflowId, savedWorkflows, versions]);

  useEffect(() => {
    if (view !== "canvas" || nodes.length === 0) return;
    const timer = setTimeout(() => {
      localStorage.setItem(AUTOSAVE_KEY, JSON.stringify({ nodes, connections, name: workflowName, id: activeWorkflowId }));
    }, 3000);
    return () => clearTimeout(timer);
  }, [nodes, connections, view, workflowName, activeWorkflowId]);

  const exportJSON = useCallback(() => {
    const data = JSON.stringify({ name: workflowName, nodes, connections }, null, 2);
    const blob = new Blob([data], { type: "application/json" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `${workflowName.replace(/\s+/g, "_")}.json`;
    a.click();
    URL.revokeObjectURL(url);
  }, [nodes, connections, workflowName]);

  const importJSON = useCallback(() => {
    const input = document.createElement("input");
    input.type = "file";
    input.accept = ".json";
    input.onchange = (e: any) => {
      const file = e.target.files[0];
      if (!file) return;
      const reader = new FileReader();
      reader.onload = (ev) => {
        try {
          const data = JSON.parse(ev.target?.result as string);
          if (data.nodes && data.connections) {
            setNodes(data.nodes);
            setConnections(data.connections);
            if (data.name) setWorkflowName(data.name);
            pushHistory(data.nodes, data.connections);
          }
        } catch {}
      };
      reader.readAsText(file);
    };
    input.click();
  }, [pushHistory]);

  const restoreVersion = useCallback((v: VersionSnapshot) => {
    setNodes(JSON.parse(JSON.stringify(v.nodes)));
    setConnections(JSON.parse(JSON.stringify(v.connections)));
    pushHistory(v.nodes, v.connections);
    setShowVersionHistory(false);
  }, [pushHistory]);

  useEffect(() => {
    if (!simulating) return;
    const interval = setInterval(() => {
      setSimulationStep(p => (p + 1) % (nodes.length + 1));
    }, 800);
    return () => clearInterval(interval);
  }, [simulating, nodes.length]);

  const dragRef = useRef<DragState>({ type: null });
  const nodesRef = useRef(nodes);
  const connectionsRef = useRef(connections);
  const cameraRef = useRef(camera);
  const zoomRef = useRef(zoom);
  nodesRef.current = nodes;
  connectionsRef.current = connections;
  cameraRef.current = camera;
  zoomRef.current = zoom;

  const NODE_WIDTH_COMPACT = 180;
  const NODE_WIDTH_EXPANDED = 320;
  const NODE_HEIGHT = 80;
  const getNodeWidth = useCallback((nodeId: string) => {
    return expandedNodes.has(nodeId) ? NODE_WIDTH_EXPANDED : NODE_WIDTH_COMPACT;
  }, [expandedNodes]);
  const getPortPosition = useCallback((node: WorkflowNode, portType: "input" | "output") => {
    const w = expandedNodes.has(node.id) ? NODE_WIDTH_EXPANDED : NODE_WIDTH_COMPACT;
    const cy = node.y + NODE_HEIGHT / 2;
    if (portType === "input") return { x: node.x - 8, y: cy };
    return { x: node.x + w + 8, y: cy };
  }, [expandedNodes]);

  const pushHistoryRef = useRef(pushHistory);
  const addNodeRef = useRef(addNode);
  const getPortPositionRef = useRef(getPortPosition);
  pushHistoryRef.current = pushHistory;
  addNodeRef.current = addNode;
  getPortPositionRef.current = getPortPosition;

  const rafRef = useRef<number | null>(null);

  useEffect(() => {
    const forceStop = () => {
      dragRef.current = { type: null };
      document.body.style.userSelect = "";
      document.body.style.cursor = "";
      document.body.style.pointerEvents = "";
      document.body.classList.remove("dragging");
      setDrag({ type: null });
      if (rafRef.current) {
        cancelAnimationFrame(rafRef.current);
        rafRef.current = null;
      }
    };

    const handleBlur = () => forceStop();
    const handleMouseLeave = (e: MouseEvent) => {
      if (!e.relatedTarget) forceStop();
    };
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.key === "Escape") forceStop();
      if (e.key === " ") setSpaceHeld(true);
    };
    const handleKeyUp = (e: KeyboardEvent) => {
      if (e.key === " ") setSpaceHeld(false);
    };

    let pendingMouseEvent: MouseEvent | null = null;

    const processFrame = () => {
      rafRef.current = null;
      const e = pendingMouseEvent;
      if (!e) return;
      pendingMouseEvent = null;

      const d = dragRef.current;
      if (!d.type) return;

      if (d.type === "canvas") {
        const newCam = {
          x: e.clientX - (d.startX || 0),
          y: e.clientY - (d.startY || 0),
        };
        cameraRef.current = newCam;
        setCamera(newCam);
      } else if (d.type === "node" && d.nodeId) {
        const rect = canvasRef.current?.getBoundingClientRect();
        if (!rect) return;
        const z = zoomRef.current;
        const cam = cameraRef.current;
        const newX = snapToGrid((e.clientX - rect.left) / z - cam.x / z - (d.offsetX || 0));
        const newY = snapToGrid((e.clientY - rect.top) / z - cam.y / z - (d.offsetY || 0));
        setNodes(prev => prev.map(n => n.id === d.nodeId ? { ...n, x: newX, y: newY } : n));
      } else if (d.type === "connecting") {
        const updated = { ...d, mouseX: e.clientX, mouseY: e.clientY };
        dragRef.current = updated;
        setDrag(updated);
      } else if (d.type === "library-drag") {
        const updated = { ...d, mouseX: e.clientX, mouseY: e.clientY };
        dragRef.current = updated;
        setDrag(updated);
      }
    };

    const onMouseMove = (e: MouseEvent) => {
      if (!dragRef.current.type) return;
      e.preventDefault();
      pendingMouseEvent = e;
      if (!rafRef.current) {
        rafRef.current = requestAnimationFrame(processFrame);
      }
    };

    const onMouseUp = (e: MouseEvent) => {
      const d = dragRef.current;
      if (!d.type) return;

      if (rafRef.current) {
        cancelAnimationFrame(rafRef.current);
        rafRef.current = null;
      }
      pendingMouseEvent = null;

      const currentNodes = nodesRef.current;
      const currentConns = connectionsRef.current;
      const cam = cameraRef.current;
      const z = zoomRef.current;

      if (d.type === "node") {
        pushHistoryRef.current(currentNodes, currentConns);
      } else if (d.type === "connecting" && d.fromNode && d.fromPort) {
        const fromPortType = d.fromPortType || (d.fromPort === "out" ? "output" : "input");
        const isFromOutput = fromPortType === "output";
        const rect = canvasRef.current?.getBoundingClientRect();
        if (rect) {
          const mx = (e.clientX - rect.left - cam.x) / z;
          const my = (e.clientY - rect.top - cam.y) / z;
          let bestDist = Infinity;
          let bestNode: WorkflowNode | null = null;
          for (const node of currentNodes) {
            if (node.id === d.fromNode) continue;
            const targetPortType = isFromOutput ? "input" : "output";
            const targetPort = node.ports.find(p => p.type === targetPortType);
            if (targetPort) {
              const portPos = getPortPositionRef.current(node, targetPortType);
              const dist = Math.sqrt((mx - portPos.x) ** 2 + (my - portPos.y) ** 2);
              if (dist < 50 && dist < bestDist) {
                bestDist = dist;
                bestNode = node;
              }
            }
          }
          if (bestNode) {
            const sourceNode = isFromOutput ? d.fromNode! : bestNode.id;
            const targetNode = isFromOutput ? bestNode.id : d.fromNode!;
            const exists = currentConns.some(
              c => (c.fromNode === sourceNode && c.toNode === targetNode) ||
                   (c.fromNode === targetNode && c.toNode === sourceNode)
            );
            if (!exists) {
              const newConn: Connection = {
                id: `conn_${Date.now()}`,
                fromNode: sourceNode,
                fromPort: "out",
                toNode: targetNode,
                toPort: "in",
              };
              const newConns = [...currentConns, newConn];
              setConnections(newConns);
              pushHistoryRef.current(currentNodes, newConns);
            }
          }
        }
      } else if (d.type === "library-drag" && d.libraryType) {
        const rect = canvasRef.current?.getBoundingClientRect();
        if (rect) {
          const x = (e.clientX - rect.left - cam.x) / z;
          const y = (e.clientY - rect.top - cam.y) / z;
          if (e.clientX > rect.left && e.clientX < rect.right && e.clientY > rect.top && e.clientY < rect.bottom) {
            addNodeRef.current(d.libraryType, x - 90, y - 30);
          }
        }
      }

      forceStop();
    };

    const handleContextMenu = () => { if (dragRef.current.type) forceStop(); };
    const handleVisChange = () => { if (document.hidden) forceStop(); };

    document.addEventListener("mousemove", onMouseMove);
    document.addEventListener("mouseup", onMouseUp);
    window.addEventListener("blur", handleBlur);
    document.addEventListener("mouseleave", handleMouseLeave);
    document.addEventListener("keydown", handleKeyDown);
    document.addEventListener("keyup", handleKeyUp);
    document.addEventListener("contextmenu", handleContextMenu);
    document.addEventListener("visibilitychange", handleVisChange);

    return () => {
      document.removeEventListener("mousemove", onMouseMove);
      document.removeEventListener("mouseup", onMouseUp);
      window.removeEventListener("blur", handleBlur);
      document.removeEventListener("mouseleave", handleMouseLeave);
      document.removeEventListener("keydown", handleKeyDown);
      document.removeEventListener("keyup", handleKeyUp);
      document.removeEventListener("contextmenu", handleContextMenu);
      document.removeEventListener("visibilitychange", handleVisChange);
      forceStop();
    };
  }, []);

  const handleCanvasMouseDown = useCallback((e: React.MouseEvent) => {
    if ((e.target as HTMLElement).closest("[data-node-card]")) return;
    document.body.style.userSelect = "none";
    document.body.style.cursor = "grabbing";
    const d: DragState = {
      type: "canvas",
      startX: e.clientX - cameraRef.current.x,
      startY: e.clientY - cameraRef.current.y,
    };
    dragRef.current = d;
    setDrag(d);
    setSelectedNode(null);
    setRightPanelOpen(false);
  }, []);

  const handleNodeMouseDown = useCallback((e: React.MouseEvent, nodeId: string) => {
    e.stopPropagation();
    const node = nodesRef.current.find(n => n.id === nodeId);
    if (!node) return;
    const rect = canvasRef.current?.getBoundingClientRect();
    if (!rect) return;
    document.body.style.userSelect = "none";
    document.body.style.cursor = "grabbing";
    const z = zoomRef.current;
    const cam = cameraRef.current;
    const d: DragState = {
      type: "node",
      nodeId,
      offsetX: (e.clientX - rect.left) / z - cam.x / z - node.x,
      offsetY: (e.clientY - rect.top) / z - cam.y / z - node.y,
    };
    dragRef.current = d;
    setDrag(d);
    setSelectedNode(nodeId);
    setRightPanelOpen(true);
  }, []);

  const handlePortMouseDown = useCallback((e: React.MouseEvent, nodeId: string, portId: string, portType: "input" | "output") => {
    e.stopPropagation();
    e.preventDefault();
    document.body.style.userSelect = "none";
    document.body.style.cursor = "crosshair";
    const d: DragState = {
      type: "connecting",
      fromNode: nodeId,
      fromPort: portId,
      fromPortType: portType,
      mouseX: e.clientX,
      mouseY: e.clientY,
    };
    dragRef.current = d;
    setDrag(d);
  }, []);

  const handleWheel = useCallback((e: React.WheelEvent) => {
    e.preventDefault();
    const delta = e.deltaY > 0 ? -0.1 : 0.1;
    setZoom(z => Math.min(2, Math.max(0.3, z + delta)));
  }, []);

  const deleteConnection = useCallback((connId: string) => {
    const newConns = connections.filter(c => c.id !== connId);
    setConnections(newConns);
    pushHistory(nodes, newConns);
  }, [connections, nodes, pushHistory]);

  const isValidDropTarget = useCallback((nodeId: string, portType: "input" | "output"): boolean => {
    if (!drag.fromNode || !drag.fromPortType) return false;
    if (drag.fromNode === nodeId) return false;
    if (drag.fromPortType === "output" && portType === "input") return true;
    if (drag.fromPortType === "input" && portType === "output") return true;
    return false;
  }, [drag.fromNode, drag.fromPortType]);

  const isInvalidDropTarget = useCallback((nodeId: string, portType: "input" | "output"): boolean => {
    if (!drag.fromNode || !drag.fromPortType) return false;
    if (drag.fromNode === nodeId) return false;
    if (drag.fromPortType === portType) return true;
    return false;
  }, [drag.fromNode, drag.fromPortType]);

  const renderConnections = () => {
    return (
      <>
        <defs>
          <filter id="conn-glow">
            <feGaussianBlur stdDeviation="3" result="blur" />
            <feMerge>
              <feMergeNode in="blur" />
              <feMergeNode in="SourceGraphic" />
            </feMerge>
          </filter>
          <filter id="conn-glow-hover">
            <feGaussianBlur stdDeviation="6" result="blur" />
            <feMerge>
              <feMergeNode in="blur" />
              <feMergeNode in="SourceGraphic" />
            </feMerge>
          </filter>
        </defs>
        {connections.map((conn) => {
          const fromNode = nodes.find(n => n.id === conn.fromNode);
          const toNode = nodes.find(n => n.id === conn.toNode);
          if (!fromNode || !toNode) return null;

          const start = getPortPosition(fromNode, "output");
          const end = getPortPosition(toNode, "input");
          const dx = Math.abs(end.x - start.x);
          const cpOffset = Math.max(dx * 0.5, 60);
          const isHovered = hoveredConnection === conn.id;
          const pathD = `M ${start.x} ${start.y} C ${start.x + cpOffset} ${start.y}, ${end.x - cpOffset} ${end.y}, ${end.x} ${end.y}`;

          const arrowSize = 8;
          const t2 = 0.97;
          const ct = 1 - t2;
          const cp1x = start.x + cpOffset;
          const cp2x = end.x - cpOffset;
          const ax = ct * ct * ct * start.x + 3 * ct * ct * t2 * cp1x + 3 * ct * t2 * t2 * cp2x + t2 * t2 * t2 * end.x;
          const ay = ct * ct * ct * start.y + 3 * ct * ct * t2 * start.y + 3 * ct * t2 * t2 * end.y + t2 * t2 * t2 * end.y;
          const angle = Math.atan2(end.y - ay, end.x - ax);
          const arrowPath = `M ${end.x} ${end.y} L ${end.x - arrowSize * Math.cos(angle - 0.4)} ${end.y - arrowSize * Math.sin(angle - 0.4)} L ${end.x - arrowSize * Math.cos(angle + 0.4)} ${end.y - arrowSize * Math.sin(angle + 0.4)} Z`;

          return (
            <g key={conn.id} data-testid={`connection-${conn.id}`}>
              <path
                d={pathD}
                fill="none"
                stroke="transparent"
                strokeWidth={24 / zoom}
                className="cursor-pointer"
                style={{ pointerEvents: "stroke" }}
                onClick={(e) => { e.stopPropagation(); deleteConnection(conn.id); }}
                onMouseEnter={() => setHoveredConnection(conn.id)}
                onMouseLeave={() => setHoveredConnection(null)}
              />
              <path
                d={pathD}
                fill="none"
                stroke="#4cc9ff"
                strokeWidth={isHovered ? 3 : 2}
                strokeLinecap="round"
                opacity={isHovered ? 1 : 0.8}
                filter={isHovered ? "url(#conn-glow-hover)" : "url(#conn-glow)"}
                className="pointer-events-none"
                style={{ transition: "stroke-width 0.15s, opacity 0.15s" }}
              />
              <path
                d={arrowPath}
                fill="#4cc9ff"
                opacity={isHovered ? 1 : 0.8}
                filter={isHovered ? "url(#conn-glow-hover)" : "url(#conn-glow)"}
                className="pointer-events-none"
              />
            </g>
          );
        })}
      </>
    );
  };

  const renderDragConnection = () => {
    if (drag.type !== "connecting" || !drag.fromNode || !drag.fromPort) return null;
    const fromNode = nodes.find(n => n.id === drag.fromNode);
    if (!fromNode) return null;
    const rect = canvasRef.current?.getBoundingClientRect();
    if (!rect) return null;

    const fromPortType = drag.fromPortType || (drag.fromPort === "out" ? "output" : "input");
    const isOutput = fromPortType === "output";
    const start = getPortPosition(fromNode, isOutput ? "output" : "input");
    const endX = ((drag.mouseX || 0) - rect.left - camera.x) / zoom;
    const endY = ((drag.mouseY || 0) - rect.top - camera.y) / zoom;

    const sx = isOutput ? start.x : endX;
    const sy = isOutput ? start.y : endY;
    const ex = isOutput ? endX : start.x;
    const ey = isOutput ? endY : start.y;
    const midX = (sx + ex) / 2;

    const cpOff = Math.max(Math.abs(ex - sx) * 0.5, 60);
    return (
      <g>
        <path
          d={`M ${sx} ${sy} C ${sx + cpOff} ${sy}, ${ex - cpOff} ${ey}, ${ex} ${ey}`}
          fill="none"
          stroke="#4cc9ff"
          strokeWidth={2}
          strokeDasharray="8 4"
          opacity={0.7}
          filter="url(#conn-glow)"
          className="pointer-events-none"
        />
      </g>
    );
  };

  const renderMinimap = () => {
    if (nodes.length === 0) return null;
    const minX = Math.min(...nodes.map(n => n.x)) - 50;
    const minY = Math.min(...nodes.map(n => n.y)) - 50;
    const maxX = Math.max(...nodes.map(n => n.x + getNodeWidth(n.id))) + 50;
    const maxY = Math.max(...nodes.map(n => n.y + NODE_HEIGHT)) + 50;
    const w = maxX - minX || 1;
    const h = maxY - minY || 1;
    const mmW = 140;
    const mmH = 90;
    const scale = Math.min(mmW / w, mmH / h);

    return (
      <div className="absolute bottom-3 right-3 z-30 rounded-lg border border-white/10 bg-[#0a0f1e]/80 backdrop-blur-md overflow-hidden" style={{ width: mmW, height: mmH }} data-testid="minimap">
        <svg width={mmW} height={mmH}>
          {nodes.map(n => (
            <rect
              key={n.id}
              x={(n.x - minX) * scale}
              y={(n.y - minY) * scale}
              width={getNodeWidth(n.id) * scale}
              height={NODE_HEIGHT * scale}
              rx={2}
              fill={getNodeColor(n.type)}
              opacity={0.6}
            />
          ))}
          {connections.map(c => {
            const fromN = nodes.find(n => n.id === c.fromNode);
            const toN = nodes.find(n => n.id === c.toNode);
            if (!fromN || !toN) return null;
            return (
              <line
                key={c.id}
                x1={(fromN.x + getNodeWidth(fromN.id) - minX) * scale}
                y1={(fromN.y + NODE_HEIGHT / 2 - minY) * scale}
                x2={(toN.x - minX) * scale}
                y2={(toN.y + NODE_HEIGHT / 2 - minY) * scale}
                stroke="#4cc9ff"
                strokeWidth={1}
                opacity={0.4}
              />
            );
          })}
        </svg>
      </div>
    );
  };

  const selectedNodeData = nodes.find(n => n.id === selectedNode);

  const canvasCursorClass = drag.type === "connecting"
    ? "cursor-crosshair"
    : drag.type === "node" || drag.type === "canvas"
    ? "cursor-grabbing"
    : spaceHeld ? "cursor-grab" : "cursor-default";

  const openWorkflow = (wf: SavedWorkflow) => {
    setNodes(JSON.parse(JSON.stringify(wf.nodes)));
    setConnections(JSON.parse(JSON.stringify(wf.connections)));
    setWorkflowName(wf.name);
    setActiveWorkflowId(wf.id);
    pushHistory(wf.nodes, wf.connections);
    setView("canvas");
  };

  const deleteWorkflow = (id: string) => {
    const newList = savedWorkflows.filter(w => w.id !== id);
    setSavedWorkflows(newList);
    saveWorkflows(newList);
  };

  const createFromTemplate = (templateType: "testnet" | "social" | "blank") => {
    let data: { nodes: WorkflowNode[]; connections: Connection[] };
    let name = "Untitled Workflow";
    if (templateType === "testnet") {
      data = buildTestnetTemplate(t);
      name = "Testnet Workflow";
    } else if (templateType === "social") {
      data = buildSocialTemplate(t);
      name = "Social Activities";
    } else {
      data = { nodes: [], connections: [] };
    }
    setNodes(data.nodes);
    setConnections(data.connections);
    setWorkflowName(name);
    setActiveWorkflowId(null);
    pushHistory(data.nodes, data.connections);
    setShowTemplateModal(false);
    setView("canvas");
  };

  if (view === "library") {
    return (
      <div className="flex flex-col h-full overflow-hidden bg-[#020617]" data-testid="workflow-builder-page">
        <div className="flex items-center justify-between gap-2 px-6 py-4 border-b border-white/5 bg-[#0a0f1e]/80 backdrop-blur-md flex-shrink-0 flex-wrap">
          <div className="flex items-center gap-3">
            <Zap className="w-5 h-5 text-primary" />
            <h2 className="text-lg font-bold tracking-wide">{t("workflow.myWorkflows")}</h2>
          </div>
          <div className="flex items-center gap-2">
            <Button size="sm" variant="outline" className="border-white/10 text-xs gap-1.5" onClick={() => setShowTemplateModal(true)} data-testid="button-new-workflow">
              <Plus className="w-3.5 h-3.5" /> {t("workflow.newWorkflow")}
            </Button>
            <Button size="sm" variant="outline" className="border-white/10 text-xs gap-1.5" onClick={importJSON} data-testid="button-import-workflow">
              <Upload className="w-3.5 h-3.5" /> {t("workflow.import")}
            </Button>
            <Button size="sm" variant="outline" className="border-white/10 text-xs gap-1.5" onClick={() => setShowTemplateModal(true)} data-testid="button-templates">
              <Layout className="w-3.5 h-3.5" /> {t("workflow.templates")}
            </Button>
            <Button size="sm" variant="outline" className="border-white/10 text-xs gap-1.5" onClick={() => { setGuideInitialSection(undefined); setShowGuide(true); }} data-testid="button-card-guide">
              <BookOpen className="w-3.5 h-3.5" /> {t("guide.title")}
            </Button>
          </div>
        </div>

        <div className="flex-1 overflow-y-auto p-6">
          {savedWorkflows.length === 0 ? (
            <div className="flex flex-col items-center justify-center h-full text-center">
              <div className="w-20 h-20 rounded-2xl bg-primary/10 flex items-center justify-center mx-auto mb-5">
                <Zap className="w-10 h-10 text-primary/40" />
              </div>
              <p className="text-base text-white/40 font-medium mb-2">{t("workflow.noWorkflows")}</p>
              <p className="text-sm text-white/20 mb-6">{t("workflow.createFirst")}</p>
              <Button size="sm" className="gap-2" onClick={() => setShowTemplateModal(true)} data-testid="button-create-first">
                <Plus className="w-4 h-4" /> {t("workflow.newWorkflow")}
              </Button>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {savedWorkflows.map(wf => (
                <div
                  key={wf.id}
                  className="rounded-xl border border-white/10 bg-[#0a0f1e]/60 backdrop-blur-md p-4 hover:border-primary/30 transition-all cursor-pointer group"
                  onClick={() => openWorkflow(wf)}
                  data-testid={`workflow-card-${wf.id}`}
                >
                  <div className="flex items-center justify-between mb-3">
                    <h3 className="text-sm font-bold text-white/90 truncate flex-1">{wf.name}</h3>
                    <div className={`px-2 py-0.5 rounded-full text-[10px] font-semibold ${
                      wf.status === "active" ? "bg-green-500/15 text-green-400" :
                      wf.status === "paused" ? "bg-yellow-500/15 text-yellow-400" :
                      "bg-white/5 text-white/40"
                    }`}>
                      {wf.status}
                    </div>
                  </div>
                  <div className="flex items-center gap-4 text-[11px] text-white/40 mb-3">
                    <span>{t("workflow.lastEdited")}: {new Date(wf.lastEdited).toLocaleDateString()}</span>
                    <span>{t("workflow.runCount")}: {wf.runCount}</span>
                  </div>
                  <div className="flex items-center gap-1.5">
                    {wf.nodes.slice(0, 5).map(n => (
                      <div key={n.id} className="w-5 h-5 rounded flex items-center justify-center" style={{ backgroundColor: `${getNodeColor(n.type)}20`, color: getNodeColor(n.type) }}>
                        {getIcon(n.icon, "w-2.5 h-2.5")}
                      </div>
                    ))}
                    {wf.nodes.length > 5 && <span className="text-[10px] text-white/30">+{wf.nodes.length - 5}</span>}
                  </div>
                  <div className="flex items-center gap-2 mt-3 opacity-0 group-hover:opacity-100 transition-opacity">
                    <Button size="sm" variant="outline" className="border-white/10 text-xs flex-1 gap-1" onClick={(e) => { e.stopPropagation(); openWorkflow(wf); }} data-testid={`button-edit-${wf.id}`}>
                      {t("common.edit")}
                    </Button>
                    <Button size="sm" variant="outline" className="border-white/10 text-xs text-red-400" onClick={(e) => { e.stopPropagation(); deleteWorkflow(wf.id); }} data-testid={`button-delete-wf-${wf.id}`}>
                      <Trash2 className="w-3 h-3" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        <AnimatePresence>
          {showTemplateModal && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="fixed inset-0 z-50 flex items-center justify-center bg-black/60 backdrop-blur-sm"
              onClick={() => setShowTemplateModal(false)}
            >
              <motion.div
                initial={{ scale: 0.95, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                exit={{ scale: 0.95, opacity: 0 }}
                className="bg-[#0a0f1e] border border-white/10 rounded-2xl p-6 max-w-lg w-full mx-4"
                onClick={e => e.stopPropagation()}
                data-testid="template-modal"
              >
                <h3 className="text-lg font-bold mb-5">{t("workflow.templateTitle")}</h3>
                <div className="space-y-3">
                  {[
                    { id: "testnet" as const, title: t("workflow.templateTestnet"), desc: t("workflow.templateTestnetDesc"), color: "#3b82f6", icon: <Wallet className="w-5 h-5" /> },
                    { id: "social" as const, title: t("workflow.templateSocial"), desc: t("workflow.templateSocialDesc"), color: "#14b8a6", icon: <Globe className="w-5 h-5" /> },
                    { id: "blank" as const, title: t("workflow.templateBlank"), desc: t("workflow.templateBlankDesc"), color: "#64748b", icon: <Plus className="w-5 h-5" /> },
                  ].map(tmpl => (
                    <button
                      key={tmpl.id}
                      onClick={() => createFromTemplate(tmpl.id)}
                      className="w-full text-left p-4 rounded-xl border border-white/10 hover:border-primary/30 bg-white/[0.02] hover:bg-white/[0.04] transition-all flex items-start gap-4 group"
                      data-testid={`template-${tmpl.id}`}
                    >
                      <div className="w-10 h-10 rounded-lg flex items-center justify-center flex-shrink-0" style={{ backgroundColor: `${tmpl.color}20`, color: tmpl.color }}>
                        {tmpl.icon}
                      </div>
                      <div className="flex-1 min-w-0">
                        <p className="text-sm font-semibold text-white/90 mb-1">{tmpl.title}</p>
                        <p className="text-xs text-white/40 leading-relaxed">{tmpl.desc}</p>
                      </div>
                      <ChevronRight className="w-4 h-4 text-white/20 mt-1 group-hover:text-white/50 transition-colors flex-shrink-0" />
                    </button>
                  ))}
                </div>
                <div className="mt-4 flex justify-end">
                  <Button size="sm" variant="outline" className="border-white/10 text-xs" onClick={() => setShowTemplateModal(false)} data-testid="button-close-template">
                    {t("common.cancel")}
                  </Button>
                </div>
              </motion.div>
            </motion.div>
          )}
        </AnimatePresence>

        <CardSettingsGuide
          isOpen={showGuide}
          onClose={() => setShowGuide(false)}
          initialSection={guideInitialSection}
        />
      </div>
    );
  }

  return (
    <div className="flex flex-col h-full overflow-hidden bg-[#020617]" data-testid="workflow-builder-page">
      <div className="flex items-center justify-between gap-2 px-4 py-2 border-b border-white/5 bg-[#0a0f1e]/80 backdrop-blur-md flex-shrink-0 z-20 flex-wrap">
        <div className="flex items-center gap-3">
          <button
            onClick={() => { saveWorkflow(); localStorage.removeItem(AUTOSAVE_KEY); setView("library"); }}
            className="text-white/40 hover:text-white transition-colors"
            data-testid="button-back-to-library"
          >
            <ArrowLeft className="w-4 h-4" />
          </button>
          <Zap className="w-5 h-5 text-primary" />
          <input
            type="text"
            value={workflowName}
            onChange={(e) => setWorkflowName(e.target.value)}
            className="text-sm font-bold tracking-wide bg-transparent border-none outline-none text-white/90 max-w-[200px]"
            data-testid="input-workflow-name"
          />
          <AnimatePresence>
            {saveFlash && (
              <motion.div
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 0, scale: 0.8 }}
                className="flex items-center gap-1 text-green-400"
              >
                <Check className="w-3.5 h-3.5" />
                <span className="text-[10px] font-medium">{t("workflow.savedSuccess")}</span>
              </motion.div>
            )}
          </AnimatePresence>
        </div>
        <div className="flex items-center gap-1 flex-wrap">
          <Button size="sm" variant="outline" className="border-white/10 text-xs gap-1.5" onClick={saveWorkflow} data-testid="button-save-workflow">
            <Save className="w-3.5 h-3.5" /> <span className="hidden sm:inline">{t("workflow.save")}</span>
          </Button>
          <Button
            size="sm"
            variant="outline"
            className={`border-white/10 text-xs gap-1.5 ${simulating ? "bg-green-500/20 text-green-400 border-green-500/30" : ""}`}
            onClick={() => { setSimulating(!simulating); setSimulationStep(0); }}
            data-testid="button-run-test"
          >
            <Play className="w-3.5 h-3.5" /> <span className="hidden sm:inline">{simulating ? t("workflow.stopSim") : t("workflow.simulate")}</span>
          </Button>
          <Button size="sm" variant="outline" className="border-white/10 text-xs gap-1.5" onClick={exportJSON} data-testid="button-export">
            <Download className="w-3.5 h-3.5" /> <span className="hidden md:inline">{t("workflow.export")}</span>
          </Button>
          <Button size="sm" variant="outline" className="border-white/10 text-xs gap-1.5" onClick={importJSON} data-testid="button-import">
            <Upload className="w-3.5 h-3.5" /> <span className="hidden md:inline">{t("workflow.import")}</span>
          </Button>
          <div className="w-px h-5 bg-white/10 mx-1 hidden sm:block" />
          <Button size="sm" variant="outline" className="border-white/10 text-xs" onClick={undo} disabled={historyIndex <= 0} data-testid="button-undo">
            <Undo2 className="w-3.5 h-3.5" />
          </Button>
          <Button size="sm" variant="outline" className="border-white/10 text-xs" onClick={redo} disabled={historyIndex >= history.length - 1} data-testid="button-redo">
            <Redo2 className="w-3.5 h-3.5" />
          </Button>
          <div className="w-px h-5 bg-white/10 mx-1 hidden sm:block" />
          <Button size="sm" variant="outline" className="border-white/10 text-xs" onClick={() => setZoom(z => Math.min(2, z + 0.2))} data-testid="button-zoom-in">
            <ZoomIn className="w-3.5 h-3.5" />
          </Button>
          <span className="text-xs text-muted-foreground w-10 text-center tabular-nums">{Math.round(zoom * 100)}%</span>
          <Button size="sm" variant="outline" className="border-white/10 text-xs" onClick={() => setZoom(z => Math.max(0.3, z - 0.2))} data-testid="button-zoom-out">
            <ZoomOut className="w-3.5 h-3.5" />
          </Button>
          <div className="w-px h-5 bg-white/10 mx-1 hidden sm:block" />
          <Button size="sm" variant="outline" className="border-white/10 text-xs" onClick={() => setShowVersionHistory(true)} data-testid="button-version-history">
            <History className="w-3.5 h-3.5" />
          </Button>
          <Button size="sm" variant="outline" className="border-white/10 text-xs gap-1.5" onClick={() => { setGuideInitialSection(undefined); setShowGuide(true); }} data-testid="button-canvas-guide">
            <HelpCircle className="w-3.5 h-3.5" />
