            <motion.div
              initial={{ width: 0, opacity: 0 }}
              animate={{ width: 220, opacity: 1 }}
              exit={{ width: 0, opacity: 0 }}
              transition={{ duration: 0.2 }}
              className="border-r border-white/5 bg-[#0a0f1e]/60 backdrop-blur-md flex-shrink-0 overflow-y-auto overflow-x-hidden z-10"
              data-testid="panel-card-library"
            >
              <div className="p-3">
                <p className="text-[10px] uppercase tracking-widest text-muted-foreground mb-3 font-semibold">{t("workflow.nodeLibrary")}</p>
                <div className="space-y-1.5">
                  {NODE_TYPES_RAW.map((nt) => (
                    <div
                      key={nt.type}
                      className="flex items-center gap-2.5 px-3 py-2.5 rounded-lg cursor-grab text-sm font-medium transition-all border border-transparent hover:border-white/10 select-none group"
                      style={{ backgroundColor: `${nt.color}08` }}
                      draggable
                      onDragStart={(e) => {
                        e.dataTransfer.setData("nodeType", nt.type);
                      }}
                      onMouseDown={(e) => {
                        if (e.button !== 0) return;
                        const d: DragState = {
                          type: "library-drag",
                          libraryType: nt.type,
                          mouseX: e.clientX,
                          mouseY: e.clientY,
                        };
                        dragRef.current = d;
                        setDrag(d);
                      }}
                      data-testid={`library-card-${nt.type}`}
                    >
                      <div
                        className="w-7 h-7 rounded-md flex items-center justify-center flex-shrink-0"
                        style={{ backgroundColor: `${nt.color}20`, color: nt.color }}
                      >
                        {getIcon(nt.icon, "w-3.5 h-3.5")}
                      </div>
                      <span className="text-xs text-white/80 group-hover:text-white transition-colors">{t(nt.labelKey)}</span>
                      <GripVertical className="w-3 h-3 text-white/20 ml-auto opacity-0 group-hover:opacity-100 transition-opacity" />
                    </div>
                  ))}
                </div>
              </div>
            </motion.div>
          )}
        </AnimatePresence>

        <button
          onClick={() => setLeftPanelOpen(!leftPanelOpen)}
          className="absolute left-0 top-1/2 -translate-y-1/2 z-20 bg-[#0a0f1e] border border-white/10 rounded-r-md p-1 text-muted-foreground hover:text-white transition-colors"
          style={{ left: leftPanelOpen ? 220 : 0 }}
          data-testid="button-toggle-left-panel"
        >
          {leftPanelOpen ? <ChevronLeft className="w-3 h-3" /> : <ChevronRight className="w-3 h-3" />}
        </button>

        <div
          ref={canvasRef}
          className={`flex-1 relative overflow-hidden bg-[#0c1222] ${canvasCursorClass}`}
          onMouseDown={(e) => {
            if (spaceHeld || !(e.target as HTMLElement).closest("[data-node-card]")) {
              handleCanvasMouseDown(e);
            }
          }}
          onWheel={handleWheel}
          onDragOver={(e) => e.preventDefault()}
          onDrop={(e) => {
            e.preventDefault();
            const type = e.dataTransfer.getData("nodeType");
            if (!type) return;
            const rect = canvasRef.current?.getBoundingClientRect();
            if (!rect) return;
            const x = (e.clientX - rect.left - camera.x) / zoom;
            const y = (e.clientY - rect.top - camera.y) / zoom;
            addNode(type, x - 90, y - 30);
          }}
          data-testid="workflow-canvas"
        >
          {/* Layer 1  Background grid */}
          <div
            className="canvas-grid absolute inset-0 pointer-events-none z-0"
            style={{
              backgroundImage: `radial-gradient(circle, rgba(76,201,255,0.06) 1px, transparent 1px)`,
              backgroundSize: `${GRID_SIZE * zoom}px ${GRID_SIZE * zoom}px`,
              backgroundPosition: `${camera.x % (GRID_SIZE * zoom)}px ${camera.y % (GRID_SIZE * zoom)}px`,
            }}
          />

          {/* Layer 2  SVG connection layer */}
          <svg
            className="absolute inset-0 z-10"
            width="100%"
            height="100%"
            style={{ overflow: "visible" }}
          >
            <g
              transform={`translate(${camera.x}, ${camera.y}) scale(${zoom})`}
              style={{ pointerEvents: "auto" }}
            >
              {renderConnections()}
              {renderDragConnection()}
            </g>
          </svg>

          {/* Layer 3  Draggable node layer */}
          <div
            className="absolute inset-0 z-20"
            style={{
              transform: `translate(${camera.x}px, ${camera.y}px) scale(${zoom})`,
