            {nodes.map((node, nodeIndex) => {
              const color = getNodeColor(node.type);
              const isSelected = selectedNode === node.id;
              const isSimActive = simulating && simulationStep === nodeIndex + 1;
              const isDraggingThis = drag.type === "node" && drag.nodeId === node.id;
              const isDisabled = !node.enabled;
              const isExpanded = expandedNodes.has(node.id);
              const isAdvanced = advancedNodes.has(node.id);
              const isNodeSim = nodeSimulating === node.id;
              const isLogExpanded = logExpandedNodes.has(node.id);
              const nodeW = isExpanded ? NODE_WIDTH_EXPANDED : NODE_WIDTH_COMPACT;

              const riskLevel = node.config.riskLevel || "safe";
              const riskColors: Record<string, { bg: string; text: string; glow: string }> = {
                safe: { bg: "bg-green-500/15", text: "text-green-400", glow: "shadow-green-500/20" },
                medium: { bg: "bg-yellow-500/15", text: "text-yellow-400", glow: "shadow-yellow-500/20" },
                risky: { bg: "bg-red-500/15", text: "text-red-400", glow: "shadow-red-500/20" },
              };
              const risk = riskColors[riskLevel] || riskColors.safe;

              const accountCount = node.config.concurrency || 10;
              const delayVal = node.config.interval || 1;
              const estMinutes = Math.round(accountCount * delayVal * (node.config.intervalUnit === "m" ? 1 : node.config.intervalUnit === "s" ? 1 / 60 : 60));
              const successCount = node.config._successCount || 0;
              const failCount = node.config._failCount || 0;

              const toggleExpand = (e: React.MouseEvent) => {
                e.stopPropagation();
                setExpandedNodes(prev => {
                  const next = new Set(prev);
                  if (next.has(node.id)) next.delete(node.id); else next.add(node.id);
                  return next;
                });
              };

              const toggleAdvanced = (e: React.MouseEvent) => {
                e.stopPropagation();
                setAdvancedNodes(prev => {
                  const next = new Set(prev);
                  if (next.has(node.id)) next.delete(node.id); else next.add(node.id);
                  return next;
                });
              };

              const toggleLogExpanded = (e: React.MouseEvent) => {
                e.stopPropagation();
                setLogExpandedNodes(prev => {
                  const next = new Set(prev);
                  if (next.has(node.id)) next.delete(node.id); else next.add(node.id);
                  return next;
                });
              };

              const simulateNode = (e: React.MouseEvent) => {
                e.stopPropagation();
                setNodeSimulating(node.id);
                setTimeout(() => setNodeSimulating(null), 2500);
              };

              const isSectionOpen = (section: string) => {
                const collapsed = collapsedSections[node.id];
                return !collapsed || !collapsed.has(section);
              };
              const toggleSection = (section: string) => (e: React.MouseEvent) => {
                e.stopPropagation();
                setCollapsedSections(prev => {
                  const nodeSet = new Set(prev[node.id] || []);
                  if (nodeSet.has(section)) nodeSet.delete(section); else nodeSet.add(section);
                  return { ...prev, [node.id]: nodeSet };
                });
              };

              const SectionHeader = ({ icon, title, sectionKey }: { icon: React.ReactNode; title: string; sectionKey: string }) => (
                <button onClick={toggleSection(sectionKey)} className="w-full flex items-center gap-2 px-2.5 py-1.5 text-[10px] uppercase tracking-widest font-semibold text-white/50 hover:text-white/70 transition-colors" data-testid={`button-section-${sectionKey}-${node.id}`}>
                  {icon}
                  <span className="flex-1 text-left">{title}</span>
                  <ChevronDown className={`w-3 h-3 transition-transform ${isSectionOpen(sectionKey) ? "rotate-180" : ""}`} />
                </button>
              );

              return (
                <div
