                  data-node-card
                  className={`absolute select-none transition-all duration-200 ${isDraggingThis ? "cursor-grabbing" : "cursor-grab"} ${isDisabled ? "opacity-40" : ""}`}
                  style={{
                    left: node.x,
                    top: node.y,
                    width: nodeW,
                    zIndex: isDraggingThis ? 100 : isSelected ? 50 : isExpanded ? 40 : 1,
                    willChange: isDraggingThis ? "transform" : undefined,
                  }}
                  onMouseDown={(e) => !spaceHeld && handleNodeMouseDown(e, node.id)}
                  data-testid={`canvas-node-${node.id}`}
                >
                  <div
                    className={`relative rounded-xl border backdrop-blur-md transition-all duration-200 ${
                      isSelected ? "ring-1 ring-offset-1 ring-offset-transparent" : ""
                    } hover:shadow-lg`}
                    style={{
                      backgroundColor: "rgba(10, 15, 30, 0.92)",
                      borderColor: isSelected ? color : `${color}30`,
                      boxShadow: isSimActive || isNodeSim
                        ? `0 0 20px ${color}50, 0 0 40px ${color}20`
