                          <div>
                            <label className="text-[9px] text-white/40 block mb-0.5">Account Group</label>
                            <PortalSelect
                              value={node.config.targetGroup || ""}
                              onChange={(v) => updateNodeConfig(node.id, "targetGroup", v)}
                              options={[{ value: "", label: "All Accounts" }, ...groups.map(g => ({ value: g.id, label: g.name }))]}
                              triggerClassName="w-full px-2 py-1.5 text-[11px]"
                              data-testid={`select-target-${node.id}`}
                            />
                          </div>

                          <div>
                            <label className="text-[9px] text-white/40 block mb-0.5">URL / Target</label>
                            <input
                              type="text"
                              value={node.config.linkUrl || ""}
                              onChange={(e) => updateNodeConfig(node.id, "linkUrl", e.target.value)}
