                              onChange={(v) => updateNodeConfig(node.id, "errorHandling", v)}
                              options={[
                                { value: "retry", label: "Continue & retry failures" },
                                { value: "skip", label: "Continue & skip failures" },
                                { value: "stop", label: "Stop on first failure" },
                              ]}
                              triggerClassName="w-full px-2 py-1.5 text-[11px]"
                              data-testid={`select-success-${node.id}`}
                            />
                          </div>
                        </div>

                        <button
