                                      <div>
                                        <label className="text-[9px] text-white/40 block mb-0.5">Stop after X failures</label>
                                        <input
                                          type="number"
                                          value={node.config.maxFailures || ""}
                                          onChange={(e) => updateNodeConfig(node.id, "maxFailures", Number(e.target.value))}
                                          className="w-full bg-white/5 border border-white/10 rounded-md px-2 py-1 text-[11px] text-white focus:outline-none focus:border-primary/50"
