                                          options={[
                                            { value: "continue", label: "Continue next" },
                                            { value: "retry", label: "Retry step" },
                                            { value: "skip", label: "Skip to end" },
                                            { value: "error-queue", label: "Send to error queue" },
                                          ]}
                                          triggerClassName="w-full px-2 py-1 text-[11px]"
                                          data-testid={`select-fail-branch-${node.id}`}
                                        />
                                      </div>
                                      <div className="flex items-center justify-between gap-2">
                                        <span className="text-[10px] text-white/50">Error queue</span>
                                        <button
                                          onClick={() => updateNodeConfig(node.id, "errorQueue", !node.config.errorQueue)}
                                          className={`w-8 h-4 rounded-full transition-all relative flex-shrink-0 ${node.config.errorQueue ? "bg-primary" : "bg-white/10"}`}
                                          data-testid={`toggle-error-queue-${node.id}`}
                                        >
                                          <div className={`absolute top-0.5 w-3 h-3 rounded-full bg-white transition-all ${node.config.errorQueue ? "left-4" : "left-0.5"}`} />
                                        </button>
                                      </div>
                                      <div className="flex items-center justify-between gap-2">
                                        <span className="text-[10px] text-white/50">Notify on failure</span>
                                        <button
                                          onClick={() => updateNodeConfig(node.id, "notifyFail", !node.config.notifyFail)}
                                          className={`w-8 h-4 rounded-full transition-all relative flex-shrink-0 ${node.config.notifyFail ? "bg-primary" : "bg-white/10"}`}
                                          data-testid={`toggle-notify-fail-${node.id}`}
                                        >
                                          <div className={`absolute top-0.5 w-3 h-3 rounded-full bg-white transition-all ${node.config.notifyFail ? "left-4" : "left-0.5"}`} />
                                        </button>
                                      </div>
                                    </div>
                                  )}
                                </div>

                                <div className="border border-white/5 rounded-lg overflow-hidden">
                                  <SectionHeader icon={<Shield className="w-3 h-3" />} title="Diagnostics" sectionKey="diagnostics" />
                                  {isSectionOpen("diagnostics") && (
                                    <div className="px-2.5 pb-2 space-y-1.5">
                                      <div className="flex items-center justify-between gap-2">
                                        <span className="text-[10px] text-white/50">Risk level</span>
                                        <RiskBadgeSelect
                                          value={riskLevel}
                                          onChange={(v) => updateNodeConfig(node.id, "riskLevel", v)}
                                          data-testid={`select-risk-adv-${node.id}`}
                                        />
                                      </div>
                                      <div className="text-[9px] text-white/30">
                                        Est: ~{accountCount} accounts / ~{estMinutes < 1 ? "<1" : estMinutes} min
                                      </div>
                                      <button
                                        onClick={toggleLogExpanded}
                                        className="w-full flex items-center gap-1.5 py-0.5 text-[10px] text-white/30 hover:text-white/50 transition-colors"
                                        data-testid={`button-logs-${node.id}`}
                                      >
                                        <Activity className="w-3 h-3" />
                                        Logs
                                        <span className="flex items-center gap-1 ml-auto">
                                          <CheckCircle2 className="w-3 h-3 text-green-500/50" />{successCount}
                                          <XCircle className="w-3 h-3 text-red-500/50" />{failCount}
                                        </span>
                                        <ChevronDown className={`w-3 h-3 transition-transform ${isLogExpanded ? "rotate-180" : ""}`} />
                                      </button>
                                      {isLogExpanded && (
                                        <div className="bg-black/30 rounded-md p-2 text-[10px] font-mono text-white/40 max-h-20 overflow-y-auto">
                                          {successCount === 0 && failCount === 0 ? (
                                            <p>No execution logs yet</p>
                                          ) : (
                                            <>
                                              {successCount > 0 && <p className="text-green-400/60">{successCount} accounts completed successfully</p>}
                                              {failCount > 0 && <p className="text-red-400/60">{failCount} accounts failed</p>}
                                            </>
                                          )}
                                        </div>
                                      )}
                                    </div>
                                  )}
                                </div>
                              </div>
                            </motion.div>
                          )}
                        </AnimatePresence>

                        <div className="flex items-center gap-1.5 pt-0.5">
                          <button
                            onClick={simulateNode}
                            className={`flex items-center gap-1 px-2 py-1 rounded-md text-[10px] font-medium transition-all border ${
                              isNodeSim
                                ? "bg-green-500/15 border-green-500/30 text-green-400"
                                : "bg-white/5 border-white/10 text-white/50 hover:text-white/80 hover:shadow-[0_0_10px_rgba(99,102,241,0.1)]"
                            }`}
                            data-testid={`button-simulate-${node.id}`}
                          >
                            <Eye className="w-3 h-3" />
                            {isNodeSim ? "Simulating..." : "Simulate 1"}
                          </button>

                          <button
                            onClick={() => toggleNodeEnabled(node.id)}
                            className={`flex items-center gap-1 px-2 py-1 rounded-md text-[10px] font-medium transition-all border ${
                              node.enabled
                                ? "bg-green-500/10 border-green-500/20 text-green-400/70"
                                : "bg-red-500/10 border-red-500/20 text-red-400/70"
                            }`}
                            data-testid={`button-toggle-enabled-${node.id}`}
                          >
                            <Power className="w-3 h-3" />
                            {node.enabled ? "On" : "Off"}
                          </button>
                        </div>
                      </div>
                    )}

                    {(isSimActive || isNodeSim) && !isExpanded && (
                      <div className="absolute bottom-0 left-0 right-0 h-0.5 bg-gradient-to-r from-transparent via-green-400 to-transparent animate-pulse rounded-b-xl" />
                    )}

                    <div
                      className="absolute -right-3 top-[40px] w-6 h-6 rounded-full flex items-center justify-center cursor-crosshair z-10 group/port"
                      onMouseDown={(e) => handlePortMouseDown(e, node.id, "out", "output")}
                      data-testid={`port-out-${node.id}`}
                    >
                      <div
                        className={`w-3.5 h-3.5 rounded-full border-2 transition-all duration-150 group-hover/port:scale-150 ${
                          drag.type === "connecting" && isValidDropTarget(node.id, "output")
                            ? "scale-[1.8] ring-4 ring-blue-400/30"
                            : drag.type === "connecting" && isInvalidDropTarget(node.id, "output")
                            ? "scale-100 opacity-30"
                            : ""
                        }`}
                        style={{
                          borderColor: drag.type === "connecting" && isInvalidDropTarget(node.id, "output") ? "#ef4444" : color,
                          backgroundColor: drag.type === "connecting" && isValidDropTarget(node.id, "output") ? color : `${color}40`,
                        }}
                      />
                    </div>
                  </div>
                </div>
              );
            })}
          </div>

          {nodes.length === 0 && (
            <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
              <div className="text-center">
                <div className="w-16 h-16 rounded-2xl bg-primary/10 flex items-center justify-center mx-auto mb-4">
                  <Zap className="w-8 h-8 text-primary/40" />
                </div>
                <p className="text-sm text-white/30 font-medium">{t("workflow.noNodes")}</p>
                <p className="text-xs text-white/20 mt-1">{t("workflow.dropHere")}</p>
              </div>
            </div>
          )}

          {renderMinimap()}
        </div>

        <AnimatePresence>
          {rightPanelOpen && selectedNodeData && (
            <motion.div
              initial={{ width: 0, opacity: 0 }}
              animate={{ width: 280, opacity: 1 }}
              exit={{ width: 0, opacity: 0 }}
              transition={{ duration: 0.2 }}
              className="border-l border-white/5 bg-[#0a0f1e]/60 backdrop-blur-md flex-shrink-0 overflow-y-auto overflow-x-hidden z-10"
              data-testid="panel-node-settings"
            >
              <div className="p-4">
                <div className="flex items-center justify-between mb-4">
                  <div className="flex items-center gap-2">
                    <div
                      className="w-7 h-7 rounded-md flex items-center justify-center"
                      style={{ backgroundColor: `${getNodeColor(selectedNodeData.type)}20`, color: getNodeColor(selectedNodeData.type) }}
                    >
                      {getIcon(selectedNodeData.icon, "w-3.5 h-3.5")}
                    </div>
                    <span className="text-sm font-bold">{selectedNodeData.label}</span>
                  </div>
                  <button
                    onClick={() => setRightPanelOpen(false)}
                    className="text-white/30 hover:text-white transition-colors"
                    data-testid="button-close-settings"
                  >
                    <X className="w-4 h-4" />
                  </button>
                </div>

                <div className="mb-5">
                  <label className="text-[10px] uppercase tracking-widest text-muted-foreground font-semibold block mb-2">{t("workflow.nodeLabel")}</label>
                  <input
                    type="text"
                    value={selectedNodeData.label}
                    onChange={(e) => renameNode(selectedNodeData.id, e.target.value)}
                    className="w-full bg-white/5 border border-white/10 rounded-lg px-3 py-2 text-sm text-white focus:outline-none focus:border-primary/50"
                    data-testid="input-node-label"
                  />
                </div>

                <div className="mb-5">
                  <div className="flex items-center justify-between">
                    <label className="text-[10px] uppercase tracking-widest text-muted-foreground font-semibold">{t("workflow.enabled")}</label>
                    <button
                      onClick={() => toggleNodeEnabled(selectedNodeData.id)}
                      className={`w-10 h-5 rounded-full transition-all relative ${
                        selectedNodeData.enabled ? "bg-green-500" : "bg-white/10"
                      }`}
                      data-testid="toggle-enabled"
                    >
                      <div className={`absolute top-0.5 w-4 h-4 rounded-full bg-white transition-all ${
                        selectedNodeData.enabled ? "left-5" : "left-0.5"
                      }`} />
                    </button>
                  </div>
                </div>

                <div className="mb-5">
                  <label className="text-[10px] uppercase tracking-widest text-muted-foreground font-semibold block mb-2">{t("workflow.interval")}</label>
                  <div className="flex gap-2">
                    <input
                      type="number"
                      value={selectedNodeData.config.interval || ""}
                      onChange={(e) => updateNodeConfig(selectedNodeData.id, "interval", Number(e.target.value))}
                      className="flex-1 bg-white/5 border border-white/10 rounded-lg px-3 py-2 text-sm text-white focus:outline-none focus:border-primary/50"
                      placeholder="4"
                      data-testid="input-interval"
                    />
                    <PortalSelect
                      value={selectedNodeData.config.intervalUnit || "h"}
                      onChange={(v) => updateNodeConfig(selectedNodeData.id, "intervalUnit", v)}
                      options={[{ value: "s", label: "sec" }, { value: "m", label: "min" }, { value: "h", label: "hr" }]}
                      triggerClassName="px-2 py-2 text-sm"
                      data-testid="select-interval-unit"
                    />
                  </div>
                </div>

                <div className="mb-5">
                  <label className="text-[10px] uppercase tracking-widest text-muted-foreground font-semibold block mb-2">{t("workflow.duration")}</label>
                  <input
                    type="number"
                    value={selectedNodeData.config.duration || ""}
                    onChange={(e) => updateNodeConfig(selectedNodeData.id, "duration", Number(e.target.value))}
                    className="w-full bg-white/5 border border-white/10 rounded-lg px-3 py-2 text-sm text-white focus:outline-none focus:border-primary/50"
                    placeholder="7"
                    data-testid="input-duration"
                  />
                </div>

                {(selectedNodeData.type === "website" || selectedNodeData.type === "apiCall") && (
                  <div className="mb-5">
                    <label className="text-[10px] uppercase tracking-widest text-muted-foreground font-semibold block mb-2">{t("workflow.linkUrl")}</label>
                    <div className="flex items-center gap-2">
                      <LinkIcon className="w-3.5 h-3.5 text-white/30 flex-shrink-0" />
                      <input
                        type="url"
                        value={selectedNodeData.config.linkUrl || ""}
                        onChange={(e) => updateNodeConfig(selectedNodeData.id, "linkUrl", e.target.value)}
                        className="flex-1 bg-white/5 border border-white/10 rounded-lg px-3 py-2 text-sm text-white focus:outline-none focus:border-primary/50"
                        placeholder="https://..."
                        data-testid="input-link-url"
                      />
                    </div>
                  </div>
                )}

                <div className="mb-5">
                  <label className="text-[10px] uppercase tracking-widest text-muted-foreground font-semibold block mb-2">{t("workflow.splitPercentage")}</label>
                  <div className="relative">
                    <input
                      type="range"
                      min="0"
                      max="100"
                      value={selectedNodeData.config.percentage || 50}
                      onChange={(e) => updateNodeConfig(selectedNodeData.id, "percentage", Number(e.target.value))}
                      className="w-full accent-primary"
                      data-testid="slider-percentage"
                    />
                    <span className="text-xs text-primary font-mono">{selectedNodeData.config.percentage || 50}%</span>
                  </div>
                </div>

                <div className="mb-5">
                  <label className="text-[10px] uppercase tracking-widest text-muted-foreground font-semibold block mb-2">{t("workflow.splitCount")}</label>
                  <input
                    type="number"
                    value={selectedNodeData.config.splitCount || 2}
                    onChange={(e) => updateNodeConfig(selectedNodeData.id, "splitCount", Number(e.target.value))}
                    className="w-full bg-white/5 border border-white/10 rounded-lg px-3 py-2 text-sm text-white focus:outline-none focus:border-primary/50"
                    min="1"
                    max="20"
                    data-testid="input-split-count"
                  />
                </div>

                <div className="mb-5">
                  <label className="text-[10px] uppercase tracking-widest text-muted-foreground font-semibold block mb-2">{t("workflow.executionMode")}</label>
                  <div className="space-y-1.5">
                    {["sequential", "parallel", "smart"].map((mode) => (
                      <button
                        key={mode}
                        onClick={() => updateNodeConfig(selectedNodeData.id, "executionMode", mode)}
                        className={`w-full text-left px-3 py-2 rounded-lg text-xs font-medium transition-all border ${
                          selectedNodeData.config.executionMode === mode
                            ? "bg-primary/15 border-primary/30 text-primary"
                            : "bg-white/5 border-white/10 text-white/60 hover:text-white"
                        }`}
                        data-testid={`button-mode-${mode}`}
                      >
                        {mode.charAt(0).toUpperCase() + mode.slice(1)}{mode === "smart" ? " Routing" : ""}
                      </button>
                    ))}
                  </div>
                </div>

                <div className="mb-5">
                  <label className="text-[10px] uppercase tracking-widest text-muted-foreground font-semibold block mb-2">{t("workflow.errorHandling")}</label>
                  <div className="space-y-1.5">
                    {[
                      { val: "skip", label: t("workflow.errorSkip"), icon: <ArrowLeftRight className="w-3 h-3" /> },
                      { val: "retry", label: t("workflow.errorRetry"), icon: <RefreshCw className="w-3 h-3" /> },
                      { val: "stop", label: t("workflow.errorStop"), icon: <AlertTriangle className="w-3 h-3" /> },
                    ].map((opt) => (
                      <button
                        key={opt.val}
                        onClick={() => updateNodeConfig(selectedNodeData.id, "errorHandling", opt.val)}
                        className={`w-full text-left px-3 py-2 rounded-lg text-xs font-medium transition-all border flex items-center gap-2 ${
                          selectedNodeData.config.errorHandling === opt.val
                            ? "bg-primary/15 border-primary/30 text-primary"
                            : "bg-white/5 border-white/10 text-white/60 hover:text-white"
                        }`}
                        data-testid={`button-error-${opt.val}`}
                      >
                        {opt.icon} {opt.label}
                      </button>
                    ))}
                  </div>
                </div>

                <div className="mb-5">
                  <label className="text-[10px] uppercase tracking-widest text-muted-foreground font-semibold block mb-2">{t("workflow.retryAttempts")}</label>
                  <input
                    type="number"
                    value={selectedNodeData.config.retryAttempts || 3}
                    onChange={(e) => updateNodeConfig(selectedNodeData.id, "retryAttempts", Number(e.target.value))}
                    className="w-full bg-white/5 border border-white/10 rounded-lg px-3 py-2 text-sm text-white focus:outline-none focus:border-primary/50"
                    min="1"
                    max="10"
                    data-testid="input-retry-attempts"
                  />
                </div>

                <div className="mb-5">
                  <div className="flex items-center justify-between">
                    <label className="text-[10px] uppercase tracking-widest text-muted-foreground font-semibold">{t("workflow.randomize")}</label>
                    <button
                      onClick={() => updateNodeConfig(selectedNodeData.id, "randomize", !selectedNodeData.config.randomize)}
                      className={`w-10 h-5 rounded-full transition-all relative ${
                        selectedNodeData.config.randomize ? "bg-primary" : "bg-white/10"
                      }`}
                      data-testid="toggle-randomize"
                    >
                      <div className={`absolute top-0.5 w-4 h-4 rounded-full bg-white transition-all ${
                        selectedNodeData.config.randomize ? "left-5" : "left-0.5"
                      }`} />
                    </button>
                  </div>
                </div>

                <div className="mb-5">
                  <label className="text-[10px] uppercase tracking-widest text-muted-foreground font-semibold block mb-2">{t("workflow.targetGroup")}</label>
                  <PortalSelect
                    value={selectedNodeData.config.targetGroup || ""}
                    onChange={(v) => updateNodeConfig(selectedNodeData.id, "targetGroup", v)}
                    options={[{ value: "", label: t("workflow.allAccounts") }, ...groups.map(g => ({ value: g.id, label: g.name }))]}
                    triggerClassName="w-full px-3 py-2 text-sm"
                    data-testid="select-target-group"
                  />
                </div>

                <button
                  onClick={() => deleteNode(selectedNodeData.id)}
                  className="w-full px-3 py-2 rounded-lg text-xs font-medium text-red-400 border border-red-500/20 bg-red-500/5 hover:bg-red-500/10 transition-all"
                  data-testid="button-delete-selected-node"
                >
                  {t("workflow.delete")}
                </button>
              </div>
            </motion.div>
          )}
        </AnimatePresence>

        <button
          onClick={() => {
            if (selectedNodeData) setRightPanelOpen(!rightPanelOpen);
          }}
          className={`absolute right-0 top-1/2 -translate-y-1/2 z-20 bg-[#0a0f1e] border border-white/10 rounded-l-md p-1 text-muted-foreground hover:text-white transition-colors ${
            !selectedNodeData ? "opacity-30 cursor-default" : ""
          }`}
          style={{ right: rightPanelOpen && selectedNodeData ? 280 : 0 }}
          data-testid="button-toggle-right-panel"
        >
          {rightPanelOpen ? <ChevronRight className="w-3 h-3" /> : <ChevronLeft className="w-3 h-3" />}
        </button>
      </div>

      {drag.type === "library-drag" && drag.libraryType && (
        <div
          className="fixed pointer-events-none z-50"
          style={{ left: (drag.mouseX || 0) - 90, top: (drag.mouseY || 0) - 20 }}
        >
          <div className="rounded-lg border border-primary/40 bg-[#0a0f1e]/90 backdrop-blur-md px-3 py-2 flex items-center gap-2 shadow-lg shadow-primary/10">
            {getIcon(NODE_TYPES_RAW.find(n => n.type === drag.libraryType)?.icon || "", "w-3.5 h-3.5 text-primary")}
            <span className="text-xs font-medium text-white/80">
              {t(NODE_TYPES_RAW.find(n => n.type === drag.libraryType)?.labelKey || "")}
            </span>
          </div>
        </div>
      )}

      <AnimatePresence>
        {showVersionHistory && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 z-50 flex items-center justify-center bg-black/60 backdrop-blur-sm"
            onClick={() => setShowVersionHistory(false)}
          >
            <motion.div
              initial={{ scale: 0.95, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.95, opacity: 0 }}
              className="bg-[#0a0f1e] border border-white/10 rounded-2xl p-6 max-w-md w-full mx-4 max-h-[60vh] flex flex-col"
              onClick={e => e.stopPropagation()}
              data-testid="version-history-modal"
            >
              <h3 className="text-lg font-bold mb-4">{t("workflow.versionHistory")}</h3>
              <div className="flex-1 overflow-y-auto space-y-2">
                {versions.length === 0 ? (
                  <p className="text-sm text-white/40 text-center py-8">No saved versions yet</p>
                ) : (
                  versions.slice().reverse().map(v => (
                    <div key={v.id} className="flex items-center justify-between p-3 rounded-lg border border-white/10 bg-white/[0.02]">
                      <div>
                        <p className="text-xs font-medium text-white/80">{new Date(v.timestamp).toLocaleString()}</p>
                        <p className="text-[10px] text-white/40">{v.nodes.length} nodes, {v.connections.length} connections</p>
                      </div>
                      <Button size="sm" variant="outline" className="border-white/10 text-xs" onClick={() => restoreVersion(v)} data-testid={`button-restore-${v.id}`}>
                        {t("workflow.restoreVersion")}
                      </Button>
                    </div>
                  ))
                )}
              </div>
              <div className="mt-4 flex justify-end">
                <Button size="sm" variant="outline" className="border-white/10 text-xs" onClick={() => setShowVersionHistory(false)}>
                  {t("common.close")}
                </Button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      <CardSettingsGuide
        isOpen={showGuide}
        onClose={() => setShowGuide(false)}
        initialSection={guideInitialSection}
      />
    </div>
  );
}

