import { useState, useCallback } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { useLocation } from "wouter";
import {
  Plus,
  Pencil,
  Trash2,
  Users,
  CheckCircle2,
  AlertTriangle,
  XCircle,
  Workflow,
  ArrowRight,
  ChevronRight,
} from "lucide-react";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Checkbox } from "@/components/ui/checkbox";
import { useI18n } from "@/i18n/i18n";
import { useGroups, type AccountGroup } from "@/contexts/GroupsContext";

function GroupStatusBadge({ status }: { status: AccountGroup["status"] }) {
  const { t } = useI18n();
  const config = {
    success: {
      label: t("status.success"),
      className: "bg-green-500/15 text-green-400 border-green-500/30",
      icon: <CheckCircle2 className="w-3 h-3" />,
    },
    partial: {
      label: t("status.partial"),
      className: "bg-yellow-500/15 text-yellow-400 border-yellow-500/30",
      icon: <AlertTriangle className="w-3 h-3" />,
    },
    failed: {
      label: t("status.failed"),
      className: "bg-red-500/15 text-red-400 border-red-500/30",
      icon: <XCircle className="w-3 h-3" />,
    },
  };
  const c = config[status];
  return (
    <Badge
      variant="outline"
      className={`${c.className} gap-1 no-default-hover-elevate`}
      data-testid={`badge-group-status-${status}`}
    >
      {c.icon}
      {c.label}
    </Badge>
  );
}

export default function AccountGroups() {
  const { t } = useI18n();
  const [, navigate] = useLocation();
  const {
    groups,
    createGroup,
    renameGroup,
    deleteGroup,
    addAccountToGroup,
    removeAccountFromGroup,
    getUnassignedAccounts,
    getGroupAccounts,
  } = useGroups();

  const [showCreateDialog, setShowCreateDialog] = useState(false);
  const [newGroupName, setNewGroupName] = useState("");
  const [renamingId, setRenamingId] = useState<string | null>(null);
  const [renameValue, setRenameValue] = useState("");
  const [deleteConfirmId, setDeleteConfirmId] = useState<string | null>(null);
  const [managingGroupId, setManagingGroupId] = useState<string | null>(null);
  const [selectedAvailable, setSelectedAvailable] = useState<Set<string>>(new Set());
  const [selectedGroup, setSelectedGroup] = useState<Set<string>>(new Set());

  const handleCreate = useCallback(() => {
    if (newGroupName.trim()) {
      createGroup(newGroupName.trim());
      setNewGroupName("");
      setShowCreateDialog(false);
    }
  }, [newGroupName, createGroup]);

  const handleRenameStart = useCallback((group: AccountGroup) => {
    setRenamingId(group.id);
    setRenameValue(group.name);
  }, []);

  const handleRenameConfirm = useCallback(() => {
    if (renamingId && renameValue.trim()) {
      renameGroup(renamingId, renameValue.trim());
    }
    setRenamingId(null);
    setRenameValue("");
  }, [renamingId, renameValue, renameGroup]);

  const handleDeleteConfirm = useCallback(() => {
    if (deleteConfirmId) {
      deleteGroup(deleteConfirmId);
      setDeleteConfirmId(null);
      if (managingGroupId === deleteConfirmId) {
        setManagingGroupId(null);
      }
    }
  }, [deleteConfirmId, deleteGroup, managingGroupId]);

  const toggleManageAccounts = useCallback((groupId: string) => {
    setManagingGroupId(prev => prev === groupId ? null : groupId);
    setSelectedAvailable(new Set());
    setSelectedGroup(new Set());
  }, []);

  const toggleAvailableSelect = useCallback((id: string) => {
    setSelectedAvailable(prev => {
      const next = new Set(prev);
      if (next.has(id)) next.delete(id);
      else next.add(id);
      return next;
    });
  }, []);

  const toggleGroupSelect = useCallback((id: string) => {
    setSelectedGroup(prev => {
      const next = new Set(prev);
      if (next.has(id)) next.delete(id);
      else next.add(id);
      return next;
    });
  }, []);

  const handleAddSelected = useCallback(() => {
    if (!managingGroupId) return;
    selectedAvailable.forEach(accId => {
      addAccountToGroup(managingGroupId, accId);
    });
    setSelectedAvailable(new Set());
  }, [managingGroupId, selectedAvailable, addAccountToGroup]);

  const handleRemoveSelected = useCallback(() => {
    if (!managingGroupId) return;
    selectedGroup.forEach(accId => {
      removeAccountFromGroup(managingGroupId, accId);
    });
    setSelectedGroup(new Set());
  }, [managingGroupId, selectedGroup, removeAccountFromGroup]);

  return (
    <div className="space-y-8">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="flex items-center justify-between gap-4 flex-wrap"
      >
        <div>
          <h1 className="text-2xl font-bold" data-testid="text-groups-title">
            {t("groups.title")}
          </h1>
          <p className="text-sm text-muted-foreground mt-1" data-testid="text-groups-subtitle">
            {t("groups.subtitle")}
          </p>
        </div>
        <Button
          onClick={() => setShowCreateDialog(true)}
          className="gap-2"
          data-testid="button-create-group"
        >
          <Plus className="w-4 h-4" />
          {t("groups.createGroup")}
        </Button>
      </motion.div>

      <AnimatePresence>
        {showCreateDialog && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 z-50 flex items-center justify-center bg-black/60 backdrop-blur-sm"
            onClick={() => setShowCreateDialog(false)}
            data-testid="dialog-create-group-overlay"
          >
            <motion.div
              initial={{ opacity: 0, scale: 0.95, y: 10 }}
              animate={{ opacity: 1, scale: 1, y: 0 }}
              exit={{ opacity: 0, scale: 0.95, y: 10 }}
              onClick={(e) => e.stopPropagation()}
              className="bg-card border border-white/10 rounded-2xl p-6 w-full max-w-md mx-4 space-y-4"
              data-testid="dialog-create-group"
            >
              <h2 className="text-lg font-semibold">{t("groups.newGroup")}</h2>
              <div className="space-y-2">
                <label className="text-sm text-muted-foreground">{t("groups.groupName")}</label>
                <Input
                  value={newGroupName}
                  onChange={(e) => setNewGroupName(e.target.value)}
                  placeholder={t("groups.enterGroupName")}
                  onKeyDown={(e) => e.key === "Enter" && handleCreate()}
                  autoFocus
                  data-testid="input-new-group-name"
                />
              </div>
              <div className="flex justify-end gap-2">
                <Button
                  variant="outline"
                  onClick={() => { setShowCreateDialog(false); setNewGroupName(""); }}
                  data-testid="button-cancel-create"
                >
                  {t("groups.cancel")}
                </Button>
                <Button
                  onClick={handleCreate}
                  disabled={!newGroupName.trim()}
                  data-testid="button-confirm-create"
                >
                  {t("groups.create")}
                </Button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      <AnimatePresence>
        {deleteConfirmId && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 z-50 flex items-center justify-center bg-black/60 backdrop-blur-sm"
            onClick={() => setDeleteConfirmId(null)}
            data-testid="dialog-delete-overlay"
          >
            <motion.div
              initial={{ opacity: 0, scale: 0.95, y: 10 }}
              animate={{ opacity: 1, scale: 1, y: 0 }}
              exit={{ opacity: 0, scale: 0.95, y: 10 }}
              onClick={(e) => e.stopPropagation()}
              className="bg-card border border-white/10 rounded-2xl p-6 w-full max-w-md mx-4 space-y-4"
              data-testid="dialog-delete-confirm"
            >
              <h2 className="text-lg font-semibold">{t("groups.delete")}</h2>
              <p className="text-sm text-muted-foreground">{t("groups.deleteConfirm")}</p>
              <div className="flex justify-end gap-2">
                <Button
                  variant="outline"
                  onClick={() => setDeleteConfirmId(null)}
                  data-testid="button-cancel-delete"
                >
                  {t("groups.cancel")}
                </Button>
                <Button
                  variant="destructive"
                  onClick={handleDeleteConfirm}
                  data-testid="button-confirm-delete"
                >
                  {t("groups.delete")}
                </Button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      {groups.length === 0 ? (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
        >
          <Card className="bg-white/[0.03] border border-white/10 rounded-2xl p-12 text-center">
            <Users className="w-12 h-12 text-muted-foreground/40 mx-auto mb-4" />
            <p className="text-muted-foreground" data-testid="text-no-groups">
              {t("groups.noGroups")}
            </p>
          </Card>
        </motion.div>
      ) : (
        <div className="grid gap-4 md:grid-cols-2 xl:grid-cols-3">
          {groups.map((group, index) => (
            <motion.div
              key={group.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.1 + index * 0.05 }}
            >
              <Card
                className={`bg-white/[0.03] border rounded-2xl overflow-visible transition-all duration-300 cursor-pointer ${
                  group.status === "failed"
                    ? "border-red-500/20 hover:border-red-500/40"
                    : group.status === "partial"
                      ? "border-yellow-500/15 hover:border-yellow-500/30"
                      : "border-white/10 hover:border-white/20"
                }`}
                data-testid={`card-group-${group.id}`}
              >
                <div
                  className="p-5 space-y-4"
                  onClick={() => navigate(`/groups/${group.id}`)}
                  data-testid={`link-group-${group.id}`}
                >
                  <div className="flex items-center justify-between gap-2">
                    <div className="flex items-center gap-3 min-w-0 flex-1">
                      <div className={`w-10 h-10 rounded-xl flex items-center justify-center flex-shrink-0 ${
                        group.status === "success" ? "bg-green-500/10" : group.status === "partial" ? "bg-yellow-500/10" : "bg-red-500/10"
                      }`}>
                        <Users className={`w-5 h-5 ${
                          group.status === "success" ? "text-green-400" : group.status === "partial" ? "text-yellow-400" : "text-red-400"
                        }`} />
                      </div>
                      <div className="min-w-0">
                        {renamingId === group.id ? (
                          <Input
                            value={renameValue}
                            onChange={(e) => setRenameValue(e.target.value)}
                            onKeyDown={(e) => {
                              if (e.key === "Enter") handleRenameConfirm();
                              if (e.key === "Escape") { setRenamingId(null); setRenameValue(""); }
                            }}
                            onBlur={handleRenameConfirm}
                            onClick={(e) => e.stopPropagation()}
                            autoFocus
                            className="h-7 text-sm"
                            data-testid={`input-rename-${group.id}`}
                          />
                        ) : (
                          <h3 className="font-semibold text-sm truncate" data-testid={`text-group-name-${group.id}`}>
                            {group.name}
                          </h3>
                        )}
                        <p className="text-xs text-muted-foreground mt-0.5">
                          {t("groups.accountCount", { count: group.accountIds.length })}
                        </p>
                      </div>
                    </div>
                    <div className="flex items-center gap-1" onClick={(e) => e.stopPropagation()}>
                      <Button
                        size="icon"
                        variant="ghost"
                        onClick={() => handleRenameStart(group)}
                        data-testid={`button-rename-${group.id}`}
                      >
                        <Pencil className="w-3.5 h-3.5" />
                      </Button>
                      <Button
                        size="icon"
                        variant="ghost"
                        onClick={() => setDeleteConfirmId(group.id)}
                        data-testid={`button-delete-${group.id}`}
                      >
                        <Trash2 className="w-3.5 h-3.5" />
                      </Button>
                    </div>
                  </div>

                  <div className="grid grid-cols-2 gap-3 text-xs">
                    <div className="space-y-0.5">
                      <span className="text-muted-foreground">{t("groups.lastActivity")}</span>
                      <p className="font-medium" data-testid={`text-last-activity-${group.id}`}>{group.lastActivity}</p>
                    </div>
                    <div className="space-y-0.5">
                      <span className="text-muted-foreground flex items-center gap-1">
                        <Workflow className="w-3 h-3" />
                        {t("groups.activeWorkflows")}
                      </span>
                      <p className="font-medium" data-testid={`text-workflows-${group.id}`}>{group.activeWorkflows}</p>
                    </div>
                    <div className="space-y-0.5">
                      <span className="text-muted-foreground">{t("groups.successRate")}</span>
                      <p className="font-medium" data-testid={`text-success-rate-${group.id}`}>{group.successRate}%</p>
                    </div>
                    <div className="space-y-0.5 flex flex-col items-start">
                      <span className="text-muted-foreground">{t("groups.status")}</span>
                      <GroupStatusBadge status={group.status} />
                    </div>
                  </div>

                  <div className="flex items-center justify-between gap-2 pt-1 border-t border-white/5">
                    <span className="text-xs text-muted-foreground flex items-center gap-1">
                      <ChevronRight className="w-3 h-3" />
                      {t("groups.overview")}
                    </span>
                    <ArrowRight className="w-4 h-4 text-muted-foreground" />
                  </div>
                </div>

                <div className="px-5 pb-4" onClick={(e) => e.stopPropagation()}>
                  <Button
                    variant="outline"
                    size="sm"
                    className="w-full border-white/10 text-xs gap-1"
                    onClick={() => toggleManageAccounts(group.id)}
                    data-testid={`button-manage-accounts-${group.id}`}
                  >
                    <Users className="w-3 h-3" />
                    {t("groups.manageAccounts")}
                  </Button>
                </div>

                <AnimatePresence>
                  {managingGroupId === group.id && (
                    <motion.div
                      initial={{ height: 0, opacity: 0 }}
                      animate={{ height: "auto", opacity: 1 }}
                      exit={{ height: 0, opacity: 0 }}
                      transition={{ duration: 0.3, ease: "easeInOut" }}
                      className="overflow-hidden"
                      data-testid={`section-manage-accounts-${group.id}`}
                    >
                      <AccountManager
                        groupId={group.id}
                        availableAccounts={getUnassignedAccounts()}
                        groupAccounts={getGroupAccounts(group.id)}
                        selectedAvailable={selectedAvailable}
                        selectedGroup={selectedGroup}
                        onToggleAvailable={toggleAvailableSelect}
                        onToggleGroup={toggleGroupSelect}
                        onAdd={handleAddSelected}
                        onRemove={handleRemoveSelected}
                        t={t}
                      />
                    </motion.div>
                  )}
                </AnimatePresence>
              </Card>
            </motion.div>
          ))}
        </div>
      )}
    </div>
  );
}

interface AccountManagerProps {
  groupId: string;
  availableAccounts: { id: string; name: string; wallet: string }[];
  groupAccounts: { id: string; name: string; wallet: string }[];
  selectedAvailable: Set<string>;
  selectedGroup: Set<string>;
  onToggleAvailable: (id: string) => void;
  onToggleGroup: (id: string) => void;
  onAdd: () => void;
  onRemove: () => void;
  t: (key: string, params?: Record<string, string | number>) => string;
}

function AccountManager({
  groupId,
  availableAccounts,
  groupAccounts,
  selectedAvailable,
  selectedGroup,
  onToggleAvailable,
  onToggleGroup,
  onAdd,
  onRemove,
  t,
}: AccountManagerProps) {
  return (
    <div className="px-5 pb-5 border-t border-white/5 pt-4">
      <div className="grid grid-cols-2 gap-3">
        <div className="space-y-2">
          <div className="flex items-center justify-between gap-2">
            <span className="text-xs font-medium text-muted-foreground">{t("groups.availableAccounts")}</span>
            <Button
              size="sm"
              variant="outline"
              className="text-[10px] border-white/10 gap-1"
              disabled={selectedAvailable.size === 0}
              onClick={onAdd}
              data-testid={`button-add-accounts-${groupId}`}
            >
              <ArrowRight className="w-3 h-3" />
              {t("groups.addToGroup")}
            </Button>
          </div>
          <div className="space-y-1 max-h-48 overflow-y-auto pr-1">
            {availableAccounts.length === 0 ? (
              <p className="text-[10px] text-muted-foreground/60 py-2 text-center">{t("groups.noAvailable")}</p>
            ) : (
              availableAccounts.map((acc) => (
                <label
                  key={acc.id}
                  className="flex items-center gap-2 py-1.5 px-2 rounded-lg bg-white/[0.02] border border-white/5 cursor-pointer text-xs"
                  data-testid={`account-available-${acc.id}`}
                >
                  <Checkbox
                    checked={selectedAvailable.has(acc.id)}
                    onCheckedChange={() => onToggleAvailable(acc.id)}
                    data-testid={`checkbox-available-${acc.id}`}
                  />
                  <div className="min-w-0">
                    <span className="font-medium truncate block">{acc.name}</span>
                    <span className="text-[10px] text-muted-foreground font-mono">{acc.wallet}</span>
                  </div>
                </label>
              ))
            )}
          </div>
        </div>

        <div className="space-y-2">
          <div className="flex items-center justify-between gap-2">
            <span className="text-xs font-medium text-muted-foreground">{t("groups.groupAccounts")}</span>
            <Button
              size="sm"
              variant="outline"
              className="text-[10px] border-white/10 gap-1"
              disabled={selectedGroup.size === 0}
              onClick={onRemove}
              data-testid={`button-remove-accounts-${groupId}`}
            >
              {t("groups.removeFromGroup")}
            </Button>
          </div>
          <div className="space-y-1 max-h-48 overflow-y-auto pr-1">
            {groupAccounts.length === 0 ? (
              <p className="text-[10px] text-muted-foreground/60 py-2 text-center">{t("groups.noAccountsInGroup")}</p>
            ) : (
              groupAccounts.map((acc) => (
                <label
                  key={acc.id}
                  className="flex items-center gap-2 py-1.5 px-2 rounded-lg bg-white/[0.02] border border-white/5 cursor-pointer text-xs"
                  data-testid={`account-group-${acc.id}`}
                >
                  <Checkbox
                    checked={selectedGroup.has(acc.id)}
                    onCheckedChange={() => onToggleGroup(acc.id)}
                    data-testid={`checkbox-group-${acc.id}`}
                  />
                  <div className="min-w-0">
                    <span className="font-medium truncate block">{acc.name}</span>
                    <span className="text-[10px] text-muted-foreground font-mono">{acc.wallet}</span>
                  </div>
                </label>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

