                  data-testid="button-copy-referral"
                >
                  {copied ? (
                    <>
                      <Check className="w-4 h-4 mr-2" />
                      {t("referrals.copied")}
                    </>
                  ) : (
                    <>
                      <Copy className="w-4 h-4 mr-2" />
                      {t("referrals.copy")}
                    </>
                  )}
                </Button>
              </div>

              <div className="flex gap-2 pt-1">
                <Button
                  size="sm"
                  variant="ghost"
                  className="gap-2"
                  data-testid="button-share-telegram"
                  onClick={() => window.open(`https://t.me/share/url?url=${encodeURIComponent(referralLink)}`, '_blank')}
                >
                  <SiTelegram className="w-4 h-4" />
                  {t("referrals.shareTelegram")}
                </Button>
                <Button
                  size="sm"
                  variant="ghost"
                  className="gap-2"
                  data-testid="button-share-x"
                  onClick={() => window.open(`https://x.com/intent/tweet?url=${encodeURIComponent(referralLink)}`, '_blank')}
                >
                  <SiX className="w-3.5 h-3.5" />
                  {t("referrals.shareX")}
                </Button>
                <Button
                  size="sm"
                  variant="ghost"
                  className="gap-2"
                  data-testid="button-share-discord"
                  onClick={() => {
                    navigator.clipboard.writeText(referralLink);
                    setCopied(true);
                    setTimeout(() => setCopied(false), 2000);
                  }}
                >
                  <SiDiscord className="w-4 h-4" />
                  {t("referrals.shareDiscord")}
                </Button>
              </div>
            </div>
          </div>
        </Card>
      </motion.div>

      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.1 }}
        className="grid grid-cols-2 md:grid-cols-4 gap-4"
      >
        {[
          { key: "referrals.activeReferrals", value: data.activeReferrals.toString(), icon: Users, color: "text-green-400" },
          { key: "referrals.totalEarned", value: `$${data.totalEarned.toFixed(2)}`, icon: DollarSign, color: "text-purple-400" },
          { key: "referrals.currentLevel", value: t(`referrals.level${data.currentLevel}`), icon: Star, color: "text-blue-400" },
          { key: "referrals.lifetimeCommission", value: `${data.lifetimeCommission}%`, icon: Percent, color: "text-amber-400" },
        ].map((stat, i) => (
          <Card key={i} className="bg-white/[0.03] border border-white/10 rounded-2xl p-5">
            <div className="flex items-center justify-between gap-2 mb-3">
              <span className="text-sm text-muted-foreground truncate">{t(stat.key)}</span>
              <stat.icon className={`w-5 h-5 ${stat.color} shrink-0`} />
            </div>
            <span className="text-2xl font-bold" data-testid={`text-stat-${i}`}>{stat.value}</span>
          </Card>
        ))}
      </motion.div>

      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.15 }}
      >
        <h2 className="text-xl font-bold mb-4" data-testid="text-levels-heading">{t("referrals.yourLevel")}</h2>
        <div className="grid md:grid-cols-4 gap-4">
          {levels.map((lvl) => {
            const isUnlocked = data.currentLevel >= lvl.level;
            const isCurrent = data.currentLevel === lvl.level;
            const LevelIcon = lvl.icon;

            return (
              <Card
                key={lvl.level}
                className={`relative border rounded-2xl p-5 transition-all duration-300 ${
                  isCurrent
                    ? `${lvl.border} bg-gradient-to-br ${lvl.glow}`
                    : isUnlocked
                    ? "bg-white/[0.03] border-white/10"
                    : "bg-white/[0.01] border-white/5 opacity-60"
                }`}
                data-testid={`card-level-${lvl.level}`}
              >
                {isCurrent && (
                  <Badge className="absolute -top-2.5 right-3 text-xs" data-testid={`badge-current-${lvl.level}`}>
                    {t("referrals.current")}
                  </Badge>
                )}
                <div className="flex items-center gap-3 mb-3">
                  <div className={`w-10 h-10 rounded-xl ${lvl.bg} flex items-center justify-center`}>
                    <LevelIcon className={`w-5 h-5 ${lvl.color}`} />
                  </div>
                  <div>
                    <h3 className="font-bold">{t(lvl.nameKey)}</h3>
                    <p className="text-xs text-muted-foreground">{t(lvl.reqKey)}</p>
                  </div>
                </div>
                <div className="space-y-1.5 mb-3">
                  {lvl.perks.map((perk, i) => (
                    <div key={i} className="flex items-center gap-2 text-sm text-muted-foreground">
                      <Check className={`w-3.5 h-3.5 ${isUnlocked ? lvl.color : "text-muted-foreground/50"}`} />
                      <span>{t(perk)}</span>
                    </div>
                  ))}
                </div>
                <div className="flex items-center gap-1.5 text-xs">
                  {isUnlocked ? (
                    <>
                      <Unlock className={`w-3.5 h-3.5 ${lvl.color}`} />
                      <span className={lvl.color}>{t("referrals.unlocked")}</span>
                    </>
                  ) : (
                    <>
                      <Lock className="w-3.5 h-3.5 text-muted-foreground/50" />
                      <span className="text-muted-foreground/50">{t("referrals.locked")}</span>
                    </>
                  )}
                </div>
              </Card>
            );
          })}
        </div>
      </motion.div>

      {!hasReferrals && (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.15 }}
        >
          <Card className="bg-white/[0.03] border border-white/10 p-8 text-center space-y-4">
            <Gift className="w-12 h-12 text-muted-foreground mx-auto" />
            <p className="text-lg font-medium text-muted-foreground" data-testid="text-empty-state">{t("referrals.emptyTitle")}</p>
            <Button variant="outline" onClick={handleCopy} data-testid="button-empty-cta">
              <Copy className="w-4 h-4 mr-2" />
              {t("referrals.emptyCta")}
            </Button>
          </Card>
        </motion.div>
      )}

      <div className="grid md:grid-cols-3 gap-6">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          className="md:col-span-2"
        >
          <h2 className="text-xl font-bold mb-4" data-testid="text-activity-heading">{t("referrals.activity")}</h2>
          <Card className="bg-white/[0.03] border border-white/10 rounded-2xl">
            <div className="overflow-x-auto">
              <table className="w-full text-sm" data-testid="table-referral-activity">
                <thead>
                  <tr className="border-b border-white/10 text-muted-foreground">
                    <th className="text-left p-4 font-medium">{t("referrals.colUserId")}</th>
                    <th className="text-left p-4 font-medium">{t("referrals.colPlan")}</th>
                    <th className="text-left p-4 font-medium">{t("referrals.colAmount")}</th>
                    <th className="text-left p-4 font-medium">{t("referrals.colReward")}</th>
                    <th className="text-left p-4 font-medium">{t("referrals.colStatus")}</th>
                  </tr>
                </thead>
                <tbody>
                  {MOCK_ACTIVITY.map((row, i) => (
                    <tr key={i} className="border-b border-white/5 last:border-0" data-testid={`row-activity-${i}`}>
                      <td className="p-4 font-mono text-xs">{row.userId}</td>
                      <td className="p-4">{row.plan}</td>
                      <td className="p-4">${row.amount.toFixed(2)}</td>
                      <td className="p-4 font-medium text-green-400">${row.reward.toFixed(2)}</td>
                      <td className="p-4">
                        <Badge variant="outline" className={`text-xs ${statusColors[row.status]}`} data-testid={`badge-status-${i}`}>
                          {t(statusKeys[row.status])}
                        </Badge>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </Card>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.25 }}
          className="space-y-6"
        >
          <div>
            <h2 className="text-xl font-bold mb-4" data-testid="text-wallet-heading">{t("referrals.creditsWallet")}</h2>
            <Card className="bg-white/[0.03] border border-white/10 rounded-2xl p-5 space-y-4">
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 rounded-xl bg-green-500/10 flex items-center justify-center">
                  <Wallet className="w-5 h-5 text-green-400" />
                </div>
                <div>
                  <p className="text-xs text-muted-foreground">{t("referrals.availableCredits")}</p>
                  <p className="text-2xl font-bold text-green-400" data-testid="text-available-credits">${data.availableCredits.toFixed(2)}</p>
                </div>
              </div>
              <div className="space-y-2 text-sm">
                <div className="flex justify-between gap-2">
                  <span className="text-muted-foreground">{t("referrals.lifetimeEarned")}</span>
                  <span className="font-medium" data-testid="text-lifetime-earned">${data.lifetimeEarned.toFixed(2)}</span>
                </div>
                <div className="flex justify-between gap-2">
                  <span className="text-muted-foreground">{t("referrals.creditsUsed")}</span>
                  <span className="font-medium" data-testid="text-credits-used">${data.creditsUsed.toFixed(2)}</span>
                </div>
              </div>
              <Button variant="outline" className="w-full gap-2" data-testid="button-apply-credits">
                <Send className="w-4 h-4" />
                {t("referrals.applyCredits")}
              </Button>
              <p className="text-xs text-muted-foreground text-center">{t("referrals.creditsTooltip")}</p>
            </Card>
          </div>

          <div>
            <h2 className="text-xl font-bold mb-4" data-testid="text-achievements-heading">{t("referrals.achievements")}</h2>
            <Card className="bg-white/[0.03] border border-white/10 rounded-2xl p-5 space-y-3">
              {ACHIEVEMENTS.map((ach) => {
                const earned = data.activeReferrals >= ach.threshold;
                const AchIcon = ach.icon;
                return (
                  <div
                    key={ach.key}
                    className={`flex items-center gap-3 p-3 rounded-xl transition-all ${
                      earned ? "bg-white/[0.03]" : "opacity-40"
                    }`}
                    data-testid={`achievement-${ach.key}`}
                  >
                    <div className={`w-9 h-9 rounded-lg ${ach.bg} flex items-center justify-center`}>
                      <AchIcon className={`w-4.5 h-4.5 ${earned ? ach.color : "text-muted-foreground/50"}`} />
                    </div>
                    <div className="flex-1">
                      <p className={`text-sm font-medium ${earned ? "" : "text-muted-foreground/50"}`}>
                        {t(`referrals.${ach.key}`)}
                      </p>
                    </div>
                    {earned && (
                      <Check className="w-4 h-4 text-green-400 shrink-0" />
                    )}
                  </div>
                );
              })}
            </Card>
          </div>
        </motion.div>
      </div>
    </div>
  );
}

