      {c.label}
    </Badge>
  );
}

function TrancheProgressBar({ success, failed, pending, total }: { success: number; failed: number; pending: number; total: number }) {
  const successPct = (success / total) * 100;
  const failedPct = (failed / total) * 100;
  const pendingPct = (pending / total) * 100;

  return (
    <div className="w-full h-2 rounded-full bg-white/5 overflow-hidden flex" data-testid="tranche-progress-bar">
      <motion.div
        className="h-full bg-green-500/70"
        initial={{ width: 0 }}
        animate={{ width: `${successPct}%` }}
        transition={{ duration: 0.6, ease: "easeOut" }}
      />
      <motion.div
        className="h-full bg-red-500/70"
        initial={{ width: 0 }}
        animate={{ width: `${failedPct}%` }}
        transition={{ duration: 0.6, ease: "easeOut", delay: 0.1 }}
      />
      <motion.div
        className="h-full bg-yellow-500/40"
        initial={{ width: 0 }}
        animate={{ width: `${pendingPct}%` }}
        transition={{ duration: 0.6, ease: "easeOut", delay: 0.2 }}
      />
    </div>
  );
}

function TrancheCard({ tranche }: { tranche: Tranche }) {
  const { t } = useI18n();
  const [expanded, setExpanded] = useState(false);
  const failedAccounts = tranche.accounts.filter(a => a.status === "failed");
  const pendingAccounts = tranche.accounts.filter(a => a.status === "pending");

  const borderGlow = tranche.status === "failed"
    ? "border-red-500/20 hover:border-red-500/40 hover:shadow-[0_0_15px_rgba(239,68,68,0.06)]"
    : tranche.status === "partial"
      ? "border-yellow-500/15 hover:border-yellow-500/30 hover:shadow-[0_0_15px_rgba(234,179,8,0.04)]"
      : "border-white/10 hover:border-white/20";

  return (
