              </div>
              <div className="min-w-0">
                <div className="flex items-center gap-2 flex-wrap">
                  <span className="font-semibold text-sm" data-testid={`text-tranche-name-${tranche.id}`}>
                    {tranche.name}
                  </span>
                  <span className="text-xs text-muted-foreground">
                    {tranche.totalAccounts} {t("dashboard.accounts")}
                  </span>
                </div>
                <div className="flex items-center gap-2 mt-0.5 flex-wrap">
                  <span className="text-xs text-muted-foreground flex items-center gap-1">
                    <Clock className="w-3 h-3" />
                    {tranche.completedAt}
                  </span>
                  <span className="text-xs text-muted-foreground/50">|</span>
                  <span className="text-xs text-muted-foreground">{tranche.workflow}</span>
                </div>
              </div>
            </div>
            <div className="flex items-center gap-3">
              <div className="hidden sm:flex items-center gap-3 text-xs mr-2">
                <span className="text-green-400/80">{tranche.success} {t("dashboard.ok")}</span>
                <span className="text-red-400/80">{tranche.failed} {t("dashboard.err")}</span>
                {tranche.pending > 0 && (
                  <span className="text-yellow-400/80">{tranche.pending} {t("dashboard.wait")}</span>
                )}
              </div>
              <TrancheStatusBadge status={tranche.status} />
              <motion.div
                animate={{ rotate: expanded ? 180 : 0 }}
                transition={{ duration: 0.2 }}
              >
                <ChevronDown className="w-4 h-4 text-muted-foreground" />
              </motion.div>
            </div>
          </div>
        </div>

        <AnimatePresence>
          {expanded && (
            <motion.div
              initial={{ height: 0, opacity: 0 }}
              animate={{ height: "auto", opacity: 1 }}
              exit={{ height: 0, opacity: 0 }}
              transition={{ duration: 0.3, ease: "easeInOut" }}
              className="overflow-hidden"
              data-testid={`tranche-details-${tranche.id}`}
