                    <span className="flex items-center gap-1.5">
                      <span className="w-2 h-2 rounded-full bg-red-500" />
                      <span className="text-muted-foreground">{t("status.failed")}:</span>
                      <span className="font-semibold text-white/80" data-testid={`text-tranche-failed-${tranche.id}`}>{tranche.failed}</span>
                    </span>
                    <span className="flex items-center gap-1.5">
                      <span className="w-2 h-2 rounded-full bg-yellow-500/60" />
                      <span className="text-muted-foreground">{t("dashboard.pending")}:</span>
                      <span className="font-semibold text-white/80" data-testid={`text-tranche-pending-${tranche.id}`}>{tranche.pending}</span>
