                <TrancheProgressBar
                  success={tranche.success}
                  failed={tranche.failed}
                  pending={tranche.pending}
                  total={tranche.totalAccounts}
                />

                {failedAccounts.length > 0 && (
                  <div className="space-y-2">
                    <span className="text-xs font-medium text-red-400/80">
                      {t("dashboard.failedAccounts")} ({failedAccounts.length})
                    </span>
                    {failedAccounts.map((acc) => (
                      <div
                        key={acc.id}
                        className="flex items-center justify-between gap-3 py-2.5 px-3 rounded-lg bg-red-500/[0.04] border border-red-500/10"
                        data-testid={`failed-account-${acc.id}`}
                      >
                        <div className="flex items-center gap-2.5 min-w-0 flex-1">
                          <XCircle className="w-3.5 h-3.5 text-red-400 flex-shrink-0" />
                          <div className="min-w-0 flex-1">
                            <div className="flex items-center gap-2 flex-wrap">
                              <span className="text-xs font-medium text-white/80" data-testid={`text-failed-name-${acc.id}`}>
                                {acc.name}
                              </span>
                              <span className="text-[10px] text-muted-foreground font-mono">
                                {acc.wallet}
                              </span>
                            </div>
                            <div className="flex items-center gap-1.5 mt-0.5 flex-wrap">
                              <span className="text-[10px] text-red-400/60">
                                {t("dashboard.step")}: {acc.failedStep}
                              </span>
                              <span className="text-[10px] text-muted-foreground/40">|</span>
                              <Tooltip>
                                <TooltipTrigger asChild>
                                  <span className="text-[10px] text-red-400/80 truncate max-w-[240px] cursor-help underline decoration-dotted decoration-red-500/30">
                                    {acc.errorShort}
                                  </span>
                                </TooltipTrigger>
                                <TooltipContent
                                  side="top"
                                  className="bg-[#0f172a] border-red-500/30 text-red-200 max-w-sm"
                                >
                                  <p className="text-xs leading-relaxed">{acc.errorFull}</p>
                                </TooltipContent>
                              </Tooltip>
                            </div>
                          </div>
                        </div>
                        <Button
                          size="sm"
                          variant="outline"
                          className="border-red-500/20 text-xs gap-1 flex-shrink-0"
                          data-testid={`button-retry-account-${acc.id}`}
                        >
                          <RotateCcw className="w-3 h-3" />
                          {t("dashboard.retryAccount")}
                        </Button>
                      </div>
                    ))}
                  </div>
                )}

                {pendingAccounts.length > 0 && (
                  <div className="space-y-2">
                    <span className="text-xs font-medium text-yellow-400/70">
                      {t("dashboard.pending")} ({pendingAccounts.length})
                    </span>
                    {pendingAccounts.map((acc) => (
                      <div
                        key={acc.id}
                        className="flex items-center gap-2.5 py-2 px-3 rounded-lg bg-yellow-500/[0.03] border border-yellow-500/10 text-xs"
                        data-testid={`pending-account-${acc.id}`}
                      >
                        <Clock className="w-3.5 h-3.5 text-yellow-400/60 flex-shrink-0" />
                        <span className="text-white/60">{acc.name}</span>
                        <span className="text-[10px] text-muted-foreground font-mono">{acc.wallet}</span>
                      </div>
                    ))}
                  </div>
                )}

                {failedAccounts.length === 0 && pendingAccounts.length === 0 && (
                  <div className="text-center py-6 text-sm text-muted-foreground/60">
                    {t("dashboard.allCompleted", { count: tranche.totalAccounts })}
                  </div>
                )}
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </Card>
    </motion.div>
  );
}

function ActivityFeed({ items }: { items: ActivityItem[] }) {
