import { useState, useRef, useEffect, useCallback } from "react";
import { motion, AnimatePresence } from "framer-motion";
import {
  User, Shield, Bell, Monitor, Zap, Database, Terminal,
  Lock, Eye, EyeOff, Trash2, Download, Upload, RotateCcw,
  Globe, Clock, ToggleLeft, ChevronDown, AlertTriangle,
  LogOut, Smartphone, X
} from "lucide-react";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Separator } from "@/components/ui/separator";
import { useI18n } from "@/i18n/i18n";
import { useAuth } from "@/hooks/use-auth";

const SETTINGS_KEY = "cetra_settings";

function loadSettings(): Record<string, any> {
  try {
    const raw = localStorage.getItem(SETTINGS_KEY);
    return raw ? JSON.parse(raw) : {};
  } catch { return {}; }
}

function persistSettings(s: Record<string, any>) {
  localStorage.setItem(SETTINGS_KEY, JSON.stringify(s));
}

interface SectionDef {
  id: string;
  titleKey: string;
  icon: React.ElementType;
  color: string;
}

const SECTIONS: SectionDef[] = [
  { id: "account", titleKey: "settings.nav.account", icon: User, color: "#3b82f6" },
  { id: "interface", titleKey: "settings.nav.interface", icon: Monitor, color: "#8b5cf6" },
  { id: "execution", titleKey: "settings.nav.execution", icon: Zap, color: "#f59e0b" },
  { id: "security", titleKey: "settings.nav.security", icon: Shield, color: "#10b981" },
  { id: "notifications", titleKey: "settings.nav.notifications", icon: Bell, color: "#ec4899" },
  { id: "data", titleKey: "settings.nav.data", icon: Database, color: "#06b6d4" },
  { id: "advanced", titleKey: "settings.nav.advanced", icon: Terminal, color: "#ef4444" },
];

function SettingRow({ label, desc, children, testId }: { label: string; desc?: string; children: React.ReactNode; testId?: string }) {
  return (
    <div className="flex items-center justify-between gap-4 py-3" data-testid={testId}>
      <div className="min-w-0 flex-1">
        <p className="text-sm font-medium text-white/90">{label}</p>
        {desc && <p className="text-xs text-white/40 mt-0.5 leading-relaxed">{desc}</p>}
      </div>
      <div className="flex-shrink-0">{children}</div>
    </div>
  );
}

function OptionSelector({ options, value, onChange, testId }: { options: { value: string; label: string; desc?: string }[]; value: string; onChange: (v: string) => void; testId?: string }) {
  return (
    <div className="flex flex-wrap gap-2" data-testid={testId}>
      {options.map(opt => (
        <button
          key={opt.value}
          onClick={() => onChange(opt.value)}
          className={`px-3 py-1.5 rounded-lg text-xs font-medium transition-all border ${
            value === opt.value
              ? "bg-primary/15 border-primary/30 text-primary"
              : "bg-white/[0.03] border-white/10 text-white/50 hover:text-white/70 hover:border-white/20"
          }`}
          title={opt.desc}
          data-testid={`${testId}-${opt.value}`}
        >
          {opt.label}
        </button>
      ))}
    </div>
  );
}

export default function SettingsPage() {
  const { t } = useI18n();
  const { user, logout } = useAuth();
  const [activeSection, setActiveSection] = useState("account");
  const [settings, setSettings] = useState<Record<string, any>>(loadSettings);
  const [showConfirmModal, setShowConfirmModal] = useState<string | null>(null);
  const [showPassword, setShowPassword] = useState(false);
  const contentRef = useRef<HTMLDivElement>(null);
  const sectionRefs = useRef<Record<string, HTMLDivElement | null>>({});

  const getSetting = (key: string, fallback: any = false) => settings[key] ?? fallback;
  const setSetting = useCallback((key: string, value: any) => {
    setSettings(prev => {
      const next = { ...prev, [key]: value };
      persistSettings(next);
      return next;
    });
  }, []);

  const scrollToSection = useCallback((id: string) => {
    setActiveSection(id);
    const el = sectionRefs.current[id];
    if (el) el.scrollIntoView({ behavior: "smooth", block: "start" });
  }, []);

  useEffect(() => {
    if (!contentRef.current) return;
    const container = contentRef.current;
    const handleScroll = () => {
      const scrollTop = container.scrollTop + 100;
      for (let i = SECTIONS.length - 1; i >= 0; i--) {
        const el = sectionRefs.current[SECTIONS[i].id];
        if (el && el.offsetTop <= scrollTop) {
          setActiveSection(SECTIONS[i].id);
          break;
        }
      }
    };
    container.addEventListener("scroll", handleScroll, { passive: true });
    return () => container.removeEventListener("scroll", handleScroll);
  }, []);

  useEffect(() => {
    if (!showConfirmModal) return;
    const handleKey = (e: KeyboardEvent) => {
      if (e.key === "Escape") setShowConfirmModal(null);
    };
    window.addEventListener("keydown", handleKey);
    return () => window.removeEventListener("keydown", handleKey);
  }, [showConfirmModal]);

  const advancedEnabled = getSetting("advancedMode", false);

  const SectionCard = ({ id, children }: { id: string; children: React.ReactNode }) => {
    const section = SECTIONS.find(s => s.id === id)!;
    const Icon = section.icon;
    return (
      <div ref={el => { sectionRefs.current[id] = el; }} data-testid={`settings-section-${id}`}>
        <Card className="bg-white/[0.02] border border-white/8 rounded-xl p-6">
          <div className="flex items-center gap-3 mb-5">
            <div className="w-9 h-9 rounded-lg flex items-center justify-center flex-shrink-0" style={{ backgroundColor: `${section.color}15`, color: section.color }}>
              <Icon className="w-5 h-5" />
            </div>
            <h3 className="text-base font-bold text-white/90">{t(section.titleKey)}</h3>
          </div>
          <div className="space-y-1">{children}</div>
        </Card>
      </div>
    );
  };

  return (
    <div className="flex h-full overflow-hidden" data-testid="settings-page">
      <div className="hidden md:flex flex-col w-52 border-r border-white/5 bg-white/[0.01] flex-shrink-0">
        <div className="px-4 py-5 border-b border-white/5">
          <h2 className="text-base font-bold text-white/90">{t("settings.title")}</h2>
          <p className="text-xs text-white/40 mt-1">{t("settings.subtitle")}</p>
        </div>
        <nav className="flex-1 py-3 overflow-y-auto">
          {SECTIONS.map(section => {
            const Icon = section.icon;
            const isActive = activeSection === section.id;
            if (section.id === "advanced" && !advancedEnabled) return null;
            return (
              <button
                key={section.id}
                onClick={() => scrollToSection(section.id)}
                className={`w-full flex items-center gap-2.5 px-4 py-2.5 text-left text-sm transition-colors ${
                  isActive
                    ? "text-white bg-white/[0.06] border-r-2 border-primary"
                    : "text-white/40 hover:text-white/60 hover:bg-white/[0.02]"
                }`}
                data-testid={`settings-nav-${section.id}`}
              >
                <Icon className="w-4 h-4 flex-shrink-0" style={{ color: isActive ? section.color : undefined }} />
                <span className="truncate">{t(section.titleKey)}</span>
              </button>
            );
          })}
        </nav>
      </div>

      <div className="md:hidden flex items-center gap-1 px-4 py-2 border-b border-white/5 overflow-x-auto flex-shrink-0 absolute top-0 left-0 right-0 z-10 bg-[#0a0f1e]/90 backdrop-blur-md">
        {SECTIONS.map(section => {
          if (section.id === "advanced" && !advancedEnabled) return null;
          const Icon = section.icon;
          const isActive = activeSection === section.id;
          return (
            <button
              key={section.id}
              onClick={() => scrollToSection(section.id)}
              className={`flex items-center gap-1.5 px-3 py-1.5 rounded-md text-xs whitespace-nowrap transition-colors flex-shrink-0 ${
                isActive ? "bg-primary/15 text-primary" : "text-white/40 hover:text-white/60"
              }`}
              data-testid={`settings-mobile-nav-${section.id}`}
            >
              <Icon className="w-3 h-3" />
              {t(section.titleKey)}
            </button>
          );
        })}
      </div>

      <div ref={contentRef} className="flex-1 overflow-y-auto scroll-smooth">
        <div className="max-w-2xl mx-auto px-6 py-8 space-y-8 md:pt-8 pt-14">

          {/* Section 1  Account */}
          <SectionCard id="account">
            <SettingRow label={t("settings.email")} desc={t("settings.emailDesc")} testId="setting-email">
              <span className="text-sm text-white/60 font-mono">{user?.email || ""}</span>
            </SettingRow>
            <Separator className="bg-white/5" />
            <SettingRow label={t("settings.changePassword")} desc={t("settings.changePasswordDesc")} testId="setting-change-password">
              <div className="flex items-center gap-2">
                <Input
                  type={showPassword ? "text" : "password"}
                  placeholder="New password"
                  className="bg-white/5 border-white/10 text-white h-9 rounded-lg w-40 text-xs"
                  data-testid="input-new-password"
                />
                <Button size="icon" variant="ghost" onClick={() => setShowPassword(!showPassword)} data-testid="button-toggle-password">
                  {showPassword ? <EyeOff className="w-3.5 h-3.5" /> : <Eye className="w-3.5 h-3.5" />}
                </Button>
              </div>
            </SettingRow>
            <Separator className="bg-white/5" />
            <SettingRow label={t("settings.twoFactor")} desc={t("settings.twoFactorDesc")} testId="setting-2fa">
              <Switch checked={getSetting("twoFactor")} onCheckedChange={(v) => setSetting("twoFactor", v)} data-testid="switch-2fa" />
            </SettingRow>
            <Separator className="bg-white/5" />
            <SettingRow label={t("settings.activeSessions")} desc={t("settings.activeSessionsDesc")} testId="setting-sessions">
              <div className="flex items-center gap-2">
                <div className="flex items-center gap-1.5 px-2.5 py-1 rounded-md bg-green-500/10 border border-green-500/20">
                  <Smartphone className="w-3 h-3 text-green-400" />
