              <Switch checked={getSetting("localExecution")} onCheckedChange={(v) => setSetting("localExecution", v)} data-testid="switch-local-exec" />
            </SettingRow>
            <Separator className="bg-white/5" />
            <SettingRow label={t("settings.dataRetention")} desc={t("settings.dataRetentionDesc")} testId="setting-retention">
