                value={getSetting("dataRetention", "30")}
                onChange={(e) => setSetting("dataRetention", e.target.value)}
                className="bg-white/5 border border-white/10 text-white text-xs rounded-lg px-3 py-2 outline-none"
                data-testid="select-retention"
              >
                <option value="7">7 {t("settings.days")}</option>
                <option value="14">14 {t("settings.days")}</option>
                <option value="30">30 {t("settings.days")}</option>
                <option value="90">90 {t("settings.days")}</option>
                <option value="365">365 {t("settings.days")}</option>
              </select>
            </SettingRow>
            <Separator className="bg-white/5" />
            <SettingRow label={t("settings.autoLogDeletion")} desc={t("settings.autoLogDeletionDesc")} testId="setting-auto-delete">
              <Switch checked={getSetting("autoLogDeletion")} onCheckedChange={(v) => setSetting("autoLogDeletion", v)} data-testid="switch-auto-delete" />
            </SettingRow>
          </SectionCard>

          {/* Section 5  Notifications */}
          <SectionCard id="notifications">
            <div className="space-y-1">
              {[
                { key: "notifErrors", label: t("settings.notifErrors"), desc: t("settings.notifErrorsDesc"), default: true },
                { key: "notifCompletion", label: t("settings.notifCompletion"), desc: t("settings.notifCompletionDesc"), default: true },
                { key: "notifBilling", label: t("settings.notifBilling"), desc: t("settings.notifBillingDesc"), default: true },
                { key: "notifReferrals", label: t("settings.notifReferrals"), desc: t("settings.notifReferralsDesc"), default: false },
                { key: "notifUpdates", label: t("settings.notifUpdates"), desc: t("settings.notifUpdatesDesc"), default: false },
              ].map((item, i, arr) => (
                <div key={item.key}>
                  <SettingRow label={item.label} desc={item.desc} testId={`setting-${item.key}`}>
                    <Switch
                      checked={getSetting(item.key, item.default)}
                      onCheckedChange={(v) => setSetting(item.key, v)}
                      data-testid={`switch-${item.key}`}
                    />
                  </SettingRow>
                  {i < arr.length - 1 && <Separator className="bg-white/5" />}
                </div>
              ))}
            </div>
            <Separator className="bg-white/5 my-4" />
            <div>
              <p className="text-xs font-semibold text-white/50 uppercase tracking-wider mb-3">{t("settings.channels")}</p>
              <div className="flex items-center gap-4">
                {[
                  { key: "channelEmail", label: t("settings.channelEmail"), default: true },
                  { key: "channelInApp", label: t("settings.channelInApp"), default: true },
                ].map(ch => (
                  <label key={ch.key} className="flex items-center gap-2 cursor-pointer" data-testid={`setting-${ch.key}`}>
                    <Switch
                      checked={getSetting(ch.key, ch.default)}
                      onCheckedChange={(v) => setSetting(ch.key, v)}
                      data-testid={`switch-${ch.key}`}
                    />
                    <span className="text-xs text-white/60">{ch.label}</span>
                  </label>
                ))}
              </div>
            </div>
          </SectionCard>

          {/* Section 6  Data Management */}
          <SectionCard id="data">
            <div className="grid grid-cols-2 gap-3">
              <Button variant="outline" className="border-white/10 text-xs gap-2 h-10 rounded-lg" onClick={() => {}} data-testid="button-export-workflows">
                <Download className="w-3.5 h-3.5" /> {t("settings.exportWorkflows")}
              </Button>
              <Button variant="outline" className="border-white/10 text-xs gap-2 h-10 rounded-lg" onClick={() => {}} data-testid="button-import-workflows">
                <Upload className="w-3.5 h-3.5" /> {t("settings.importWorkflows")}
              </Button>
              <Button variant="outline" className="border-white/10 text-xs gap-2 h-10 rounded-lg" onClick={() => {}} data-testid="button-backup-settings">
                <Database className="w-3.5 h-3.5" /> {t("settings.backupSettings")}
              </Button>
              <Button variant="outline" className="border-white/10 text-xs gap-2 h-10 rounded-lg text-red-400" onClick={() => setShowConfirmModal("reset")} data-testid="button-reset-env">
                <RotateCcw className="w-3.5 h-3.5" /> {t("settings.resetEnvironment")}
              </Button>
            </div>
          </SectionCard>

          {/* Section 7  Advanced Mode */}
          <div ref={el => { sectionRefs.current["advanced"] = el; }} data-testid="settings-section-advanced">
            <Card className="bg-white/[0.02] border border-white/8 rounded-xl p-6">
              <div className="flex items-center gap-3 mb-5">
                <div className="w-9 h-9 rounded-lg flex items-center justify-center flex-shrink-0" style={{ backgroundColor: "#ef444415", color: "#ef4444" }}>
                  <Terminal className="w-5 h-5" />
                </div>
                <h3 className="text-base font-bold text-white/90">{t("settings.nav.advanced")}</h3>
              </div>
              <SettingRow label={t("settings.enableAdvanced")} desc={t("settings.enableAdvancedDesc")} testId="setting-advanced-toggle">
                <Switch checked={advancedEnabled} onCheckedChange={(v) => setSetting("advancedMode", v)} data-testid="switch-advanced-mode" />
              </SettingRow>

              <AnimatePresence>
                {advancedEnabled && (
                  <motion.div
                    initial={{ opacity: 0, height: 0 }}
                    animate={{ opacity: 1, height: "auto" }}
                    exit={{ opacity: 0, height: 0 }}
                    transition={{ duration: 0.2 }}
                    className="overflow-hidden"
                  >
                    <Separator className="bg-white/5 my-2" />
                    <SettingRow label={t("settings.debugLogs")} desc={t("settings.debugLogsDesc")} testId="setting-debug-logs">
                      <Switch checked={getSetting("debugLogs")} onCheckedChange={(v) => setSetting("debugLogs", v)} data-testid="switch-debug-logs" />
                    </SettingRow>
                    <Separator className="bg-white/5" />
                    <SettingRow label={t("settings.experimentalFeatures")} desc={t("settings.experimentalFeaturesDesc")} testId="setting-experimental">
                      <Switch checked={getSetting("experimentalFeatures")} onCheckedChange={(v) => setSetting("experimentalFeatures", v)} data-testid="switch-experimental" />
                    </SettingRow>
                    <Separator className="bg-white/5" />
                    <SettingRow label={t("settings.extendedDiagnostics")} desc={t("settings.extendedDiagnosticsDesc")} testId="setting-diagnostics">
                      <Switch checked={getSetting("extendedDiagnostics")} onCheckedChange={(v) => setSetting("extendedDiagnostics", v)} data-testid="switch-diagnostics" />
                    </SettingRow>
                  </motion.div>
                )}
              </AnimatePresence>
            </Card>
          </div>

          <div className="pb-8" />
        </div>
      </div>

      {/* Confirmation Modal */}
      <AnimatePresence>
        {showConfirmModal && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 z-50 flex items-center justify-center bg-black/60 backdrop-blur-sm"
            onClick={() => setShowConfirmModal(null)}
            data-testid="confirm-modal-overlay"
          >
            <motion.div
              initial={{ scale: 0.95, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.95, opacity: 0 }}
              className="bg-[#0a0f1e] border border-white/10 rounded-xl p-6 max-w-sm w-full mx-4"
              onClick={e => e.stopPropagation()}
              data-testid="confirm-modal"
            >
              <div className="flex items-center gap-3 mb-4">
                <div className="w-10 h-10 rounded-lg bg-red-500/10 flex items-center justify-center">
                  <AlertTriangle className="w-5 h-5 text-red-400" />
                </div>
                <h3 className="text-base font-bold text-white/90">{t("settings.confirmTitle")}</h3>
              </div>
              <p className="text-sm text-white/50 mb-6 leading-relaxed">{t("settings.confirmResetDesc")}</p>
              <div className="flex items-center gap-3 justify-end">
                <Button size="sm" variant="outline" className="border-white/10 text-xs" onClick={() => setShowConfirmModal(null)} data-testid="button-confirm-cancel">
                  {t("common.cancel")}
                </Button>
                <Button size="sm" className="bg-red-500/20 text-red-400 border border-red-500/30 text-xs gap-1.5" onClick={() => {
                  localStorage.removeItem(SETTINGS_KEY);
                  setSettings({});
                  setShowConfirmModal(null);
                }} data-testid="button-confirm-reset">
                  <Trash2 className="w-3 h-3" /> {t("settings.confirmReset")}
                </Button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

