import { useState } from "react";
import { motion } from "framer-motion";
import {
  CreditCard,
  CheckCircle2,
  ArrowRight,
  Crown,
  Shield,
  Brain,
  Server,
  Rocket,
  HeartPulse,
} from "lucide-react";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useI18n } from "@/i18n/i18n";

const MONTHLY_PRICE = 19.99;

const PLANS = [
  {
    id: "monthly",
    nameKey: "subscription.monthly",
    billingKey: "subscription.billedMonthly",
    totalPrice: 19.99,
    months: 1,
    popular: false,
    borderColor: "border-blue-500/20",
    accentColor: "text-blue-400",
    badgeBg: "bg-blue-500/10 text-blue-400 border-blue-500/20",
  },
  {
    id: "3months",
    nameKey: "subscription.3months",
    billingKey: "subscription.billedEvery3Months",
    totalPrice: 50.99,
    months: 3,
    popular: true,
    borderColor: "border-primary/30",
    accentColor: "text-indigo-400",
    badgeBg: "bg-indigo-500/10 text-indigo-400 border-indigo-500/20",
  },
  {
    id: "yearly",
    nameKey: "subscription.yearly",
    billingKey: "subscription.billedAnnually",
    totalPrice: 154.99,
    months: 12,
    popular: false,
    borderColor: "border-violet-500/20",
    accentColor: "text-violet-400",
    badgeBg: "bg-violet-500/10 text-violet-400 border-violet-500/20",
  },
];

const FEATURES = [
  "subscription.feat.unlimitedAccounts",
  "subscription.feat.highSpeed",
  "subscription.feat.priorityQueue",
  "subscription.feat.advancedErrors",
  "subscription.feat.workflowAnalytics",
  "subscription.feat.aiAssistant",
  "subscription.feat.premiumSupport",
];

function calcSavingsPercent(totalPrice: number, months: number) {
  const fullPrice = MONTHLY_PRICE * months;
  return Math.round(((fullPrice - totalPrice) / fullPrice) * 100);
}

const WHY_UPGRADE = [
  { icon: Rocket, titleKey: "subscription.why.fasterExec", descKey: "subscription.why.fasterExecDesc", color: "text-blue-400", bg: "bg-blue-500/10" },
  { icon: HeartPulse, titleKey: "subscription.why.smarterRecovery", descKey: "subscription.why.smarterRecoveryDesc", color: "text-emerald-400", bg: "bg-emerald-500/10" },
  { icon: Brain, titleKey: "subscription.why.aiAssist", descKey: "subscription.why.aiAssistDesc", color: "text-violet-400", bg: "bg-violet-500/10" },
  { icon: Server, titleKey: "subscription.why.priorityInfra", descKey: "subscription.why.priorityInfraDesc", color: "text-primary", bg: "bg-primary/10" },
  { icon: Shield, titleKey: "subscription.why.premiumReliability", descKey: "subscription.why.premiumReliabilityDesc", color: "text-amber-400", bg: "bg-amber-500/10" },
];

export default function Subscription() {
  const { t } = useI18n();
  const [currentPlan] = useState("monthly");

  return (
    <div className="space-y-10 pb-8" data-testid="subscription-page">
      <div>
        <h1 className="text-3xl font-bold mb-2" data-testid="text-subscription-title">
          {t("subscription.title")}
        </h1>
        <p className="text-muted-foreground" data-testid="text-subscription-subtitle">
          {t("subscription.subtitle")}
        </p>
      </div>

      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.1 }}
      >
        <Card className="border-primary/20 p-6" data-testid="card-current-plan">
          <div className="flex flex-col md:flex-row items-start md:items-center justify-between gap-6">
            <div className="flex items-start gap-4">
              <div className="w-12 h-12 rounded-md bg-primary/10 flex items-center justify-center flex-shrink-0">
                <CreditCard className="w-6 h-6 text-primary" />
              </div>
              <div>
                <div className="flex items-center gap-3 mb-1 flex-wrap">
                  <h3 className="text-lg font-bold" data-testid="text-current-plan-name">
                    {t(`subscription.${currentPlan}`)}
                  </h3>
                  <Badge className={`${PLANS.find(p => p.id === currentPlan)?.badgeBg}`} data-testid="badge-plan-active">
                    {t("subscription.active")}
                  </Badge>
                </div>
                <p className="text-sm text-muted-foreground mt-1">
                  {t("subscription.allPlansUnlimited")}
                </p>
              </div>
            </div>
            <Button data-testid="button-upgrade-header">
              {t("subscription.upgradePlan")}
              <ArrowRight className="ml-2 w-4 h-4" />
            </Button>
          </div>
        </Card>
      </motion.div>

      <div
        className="grid grid-cols-1 md:grid-cols-3 gap-6 max-w-5xl mx-auto"
        data-testid="pricing-grid"
      >
        {PLANS.map((plan, index) => {
          const isActive = plan.id === currentPlan;
          const perMonth = +(plan.totalPrice / plan.months).toFixed(2);
          const savings = plan.months > 1 ? calcSavingsPercent(plan.totalPrice, plan.months) : 0;

          return (
            <motion.div
              key={plan.id}
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.15 + index * 0.08 }}
              className="relative"
            >
              {plan.popular && (
                <div className="absolute -top-3 left-1/2 -translate-x-1/2 z-10">
                  <Badge className="bg-primary text-primary-foreground border-primary shadow-lg shadow-primary/25" data-testid="badge-most-popular">
                    <Crown className="w-3 h-3 mr-1" />
                    {t("subscription.mostPopular")}
                  </Badge>
                </div>
              )}
              <Card
                className={`relative overflow-visible p-0 transition-all duration-300 h-full ${
                  plan.popular
                    ? "border-primary/40 shadow-lg shadow-primary/10"
                    : plan.borderColor
                }`}
                style={
                  plan.popular
                    ? { boxShadow: "0 0 30px rgba(99,102,241,0.15), 0 0 60px rgba(99,102,241,0.05)" }
                    : undefined
                }
                data-testid={`card-plan-${plan.id}`}
              >
                <div className="p-6 space-y-5 flex flex-col h-full">
                  <div>
                    <h3 className={`text-xl font-bold ${plan.accentColor}`} data-testid={`text-plan-name-${plan.id}`}>
                      {t(plan.nameKey)}
                    </h3>
                    <p className="text-sm text-muted-foreground mt-1">
                      {t(plan.billingKey)}
                    </p>
                  </div>

                  <div>
                    <div className="flex items-baseline gap-1">
                      <span className="text-3xl font-bold" data-testid={`text-plan-price-${plan.id}`}>
                        ${plan.totalPrice}
                      </span>
                      {plan.months > 1 && (
                        <span className="text-muted-foreground text-sm">
                          (${perMonth.toFixed(2)}/{t("subscription.perMonth")})
                        </span>
                      )}
                      {plan.months === 1 && (
                        <span className="text-muted-foreground text-sm">
                          /{t("subscription.perMonth")}
                        </span>
                      )}
                    </div>
                    {savings > 0 && (
                      <p className="text-sm text-emerald-400 font-medium mt-1.5" data-testid={`text-savings-${plan.id}`}>
                        {t("subscription.saveVsMonthly").replace("{percent}", String(savings))}
                      </p>
                    )}
                  </div>

                  <div className="space-y-2.5 flex-1">
                    {FEATURES.map((featureKey) => (
                      <div
                        key={featureKey}
                        className="flex items-start gap-2.5"
                      >
                        <CheckCircle2 className="w-4 h-4 text-emerald-400 mt-0.5 flex-shrink-0" />
                        <span className="text-sm text-muted-foreground">
                          {t(featureKey)}
                        </span>
                      </div>
                    ))}
                  </div>

                  <div className="pt-2">
                    {isActive ? (
                      <Button
                        variant="outline"
                        className="w-full"
                        disabled
                        data-testid={`button-current-plan-${plan.id}`}
                      >
                        {t("subscription.currentPlanBadge")}
                      </Button>
                    ) : (
                      <Button
                        className="w-full"
                        variant={plan.popular ? "default" : "outline"}
                        data-testid={`button-upgrade-${plan.id}`}
                      >
                        {t("subscription.upgradePlan")}
                        <ArrowRight className="ml-2 w-4 h-4" />
                      </Button>
                    )}
                  </div>
                </div>
              </Card>
            </motion.div>
          );
        })}
      </div>

      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.5 }}
      >
        <Card className="p-6" data-testid="card-why-upgrade">
          <h3 className="text-lg font-bold mb-6" data-testid="text-why-upgrade-title">
            {t("subscription.whyUpgrade")}
          </h3>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-6">
            {WHY_UPGRADE.map((reason) => (
              <div
                key={reason.titleKey}
                className="flex flex-col items-center text-center gap-3"
                data-testid={`upgrade-reason-${reason.titleKey.split(".").pop()}`}
              >
                <div className={`w-10 h-10 rounded-md ${reason.bg} flex items-center justify-center`}>
                  <reason.icon className={`w-5 h-5 ${reason.color}`} />
                </div>
                <div>
                  <p className="text-sm font-semibold mb-1">{t(reason.titleKey)}</p>
                  <p className="text-xs text-muted-foreground">
                    {t(reason.descKey)}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </Card>
      </motion.div>

      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.6 }}
        className="text-center space-y-3"
      >
        <p className="text-sm font-medium" data-testid="text-payment-methods-title">
          {t("subscription.paymentMethods")}
        </p>
        <div className="flex items-center justify-center gap-4 flex-wrap">
          {[
            { label: "BTC", color: "text-amber-400" },
            { label: "ETH", color: "text-blue-400" },
            { label: "USDT", color: "text-emerald-400" },
            { label: "VISA", color: "text-blue-300" },
          ].map((method) => (
            <div
              key={method.label}
              className="flex items-center gap-1.5 px-3 py-1.5 rounded-md border border-border/50 bg-card/50"
              data-testid={`payment-method-${method.label.toLowerCase()}`}
            >
              <CreditCard className={`w-4 h-4 ${method.color}`} />
              <span className="text-xs font-medium text-muted-foreground">
                {method.label}
              </span>
            </div>
          ))}
        </div>
        <p className="text-xs text-muted-foreground">
          {t("subscription.cryptoPayments")}
        </p>
      </motion.div>
    </div>
  );
}

